import React from 'react';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { useTheme } from '@material-ui/core/styles';
import useMediaQuery from '@material-ui/core/useMediaQuery';

function SignIn() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [userEmail, setUserEmail] = React.useState('');
  const [userPassword, setUserPassowrd] = React.useState('');
  return (
    <div>
      <Container
        style={{
          border: '1px solid #41A58D',
          marginTop: 170,
          width: '90%',
          borderRadius: 20,
          boxShadow: '0px 0px 50px 1px #0000001A',
        }}
        maxWidth='md'
      >
        <div style={{ paddingTop: 40, paddingBottom: 40 }}>
          <Grid xs={12}>
            <Typography
              style={{
                fontSize: 25,
                fontWeight: 500,
                color: '#41A58D',
              }}
            >
              Izze admin Pannel
            </Typography>
            <div>
              <Typography
                style={{ marginTop: 10, fontSize: 20, fontWeight: 500 }}
              >
                Login Here
              </Typography>
              <TextField
                style={{ marginTop: 20, width: '70%' }}
                id='outlined-basic'
                label='Enter Email'
                variant='outlined'
                value={userEmail}
                onChange={(e) => setUserEmail(e.target.value)}
              />
            </div>
            <div>
              <TextField
                style={
                  isMobile
                    ? { marginTop: 40, width: '100%' }
                    : { marginTop: 40, width: '70%' }
                }
                id='outlined-basic'
                type='password'
                label='Enter Password'
                variant='outlined'
                value={userPassword}
                onChange={(e) => setUserPassowrd(e.target.value)}
              />
            </div>
            <div style={{ marginTop: 40 }}>
              <div
                style={{
                  display: 'flex',
                  justifyContent: 'space-evenly',
                  alignItems: 'center',
                }}
              >
                <Button
                  style={
                    isMobile
                      ? {
                          backgroundColor: '#41A58D',
                          color: 'white',
                          textTransform: 'capitalize',
                          width: 'auto',
                        }
                      : {
                          backgroundColor: '#41A58D',
                          color: 'white',
                          textTransform: 'capitalize',
                          width: '20%',
                        }
                  }
                >
                  Sign-Up
                </Button>
                <Button
                  style={
                    isMobile
                      ? {
                          backgroundColor: '#41A58D',
                          color: 'white',
                          textTransform: 'capitalize',
                          width: 'auto',
                        }
                      : {
                          backgroundColor: '#41A58D',
                          color: 'white',
                          textTransform: 'capitalize',
                          width: '20%',
                        }
                  }
                >
                  Sign-In
                </Button>
                <Typography style={{ textDecoration: 'underline' }}>
                  Forgot Password ?
                </Typography>
              </div>
            </div>
          </Grid>
        </div>
      </Container>
    </div>
  );
}
export default SignIn;
