import React from 'react';
import {
  makeStyles,
  Avatar,
  Card,
  CardActions,
  CardContent,
  InputLabel,
  Dialog,
  DialogActions,
  DialogContent,
  FormControl,
  FormHelperText,
  Grid,
  IconButton,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Select,
} from '@material-ui/core';
import Autocomplete from '@material-ui/lab/Autocomplete';
import AddIcon from '@material-ui/icons/Add';
import Page from '../../components/Page/Page';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { Formik } from 'formik';
import * as Yup from 'yup';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination } from '@material-ui/lab';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  themeButton: {
    color: theme.palette.white,
    backgroundColor: theme.palette.green.main,
    '&:hover': {
      backgroundColor: theme.palette.green.dark,
    },
  },
  widthAndHeight: {
    width: '100%',
    height: '100%',
  },
  addIngredients: {
    width: '100%',
    height: theme.spacing(6.5),
    backgroundColor: theme.palette.green.main,
    color: 'white',
    padding: theme.spacing(2),
  },
  lColor: {
    color: 'white',
  },
  tabCard: {
    marginTop: theme.spacing(3),
  },
  content: {
    padding: 0,
  },
  jCenter: {
    display: 'flex',
    justifyContent: 'center',
  },
  sEvenly: {
    display: 'flex',
    justifyContent: 'space-evenly',
  },
  iconPadd: {
    padding: 5,
  },
  actions: {
    padding: theme.spacing(1),
    justifyContent: 'center',
  },
}));

function SkinCarePlan() {
  const classes = useStyles();
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Skin Care Plan',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const closeAddEditDialog = () => {
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  return (
    <div className={classes.root}>
      <Page title='Skin Care Plan' />

      {/* =======Header====== */}
      <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
        <Grid item>
          <Typography component='h1' variant='h3'>
            Skin Care Plan
          </Typography>
        </Grid>
        <Grid item>
          <Button
            variant='contained'
            onClick={() => openAddDialog()}
            className={classes.themeButton}
            startIcon={<AddIcon />}
          >
            Add
          </Button>
        </Grid>
      </Grid>

      {/* =============Search======== */}
      <Grid container spacing={3}>
        <Grid item>
          <Paper elevation={0}>
            <TextField
              fullWidth
              size='small'
              placeholder='Name'
              variant='outlined'
              name='state'
            />
          </Paper>
        </Grid>
      </Grid>

      {/* ========Table With Pagination========= */}
      <Card className={classes.tabCard}>
        <CardContent className={classes.content}>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell align='center'>#</TableCell>
                  <TableCell align='center'>Image</TableCell>
                  <TableCell align='center'>Name</TableCell>
                  <TableCell align='center'>Description</TableCell>
                  <TableCell align='center'>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                <TableRow hover>
                  <TableCell align='center'>1</TableCell>
                  <TableCell align='center'>
                    <div className={classes.jCenter}>
                      <Avatar variant='square' src={''} />
                    </div>
                  </TableCell>
                  <TableCell align='center'>
                    1 Anti-Aging Cocoa Face Mask
                  </TableCell>
                  <TableCell align='center'>
                    A decadent, antioxidant-packed cocoa mask replenishes
                    moisture and gives skin a restored, youthful appearance. "As
                    skin ages
                  </TableCell>
                  <TableCell align='center'>
                    <div className={classes.sEvenly}>
                      <Tooltip title='View' arrow>
                        <IconButton
                          className={classes.iconPadd}
                          //   onClick={() => openViewDialog(data)}
                        >
                          <CenterFocusStrongIcon color='primary' />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title='Edit' arrow>
                        <IconButton
                          className={classes.iconPadd}
                          //   onClick={() => openEditDialog(data)}
                        >
                          <EditIcon color='action' />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title='Delete' arrow>
                        <IconButton
                          className={classes.iconPadd}
                          //   onClick={() => onDelete(data)}
                        >
                          <DeleteIcon color='secondary' />
                        </IconButton>
                      </Tooltip>
                    </div>
                  </TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>

        <CardActions className={classes.actions}>
          <Pagination
            count={10}
            page={1}
            // onChange={onPageChange}
          />
        </CardActions>
      </Card>

      {/* ==========Add & Edit Dialoge======== */}

      <AddEditDialog {...addEditDialog} onClose={closeAddEditDialog} />
    </div>
  );
}

const AddEditDialog = (props: any) => {
  const { isEdit, isOpen, okBtnText = 'OK', onClose, title } = props;
  const classes = useStyles();
  const initialFormValues = {
    skinType: '',
    skinIssues: '',
    recipe: '',
  };

  const onSubmit = () => {
    alert('submmitting');
  };

  return (
    <div>
      <Dialog
        open={isOpen}
        disableBackdropClick
        disableEscapeKeyDown
        fullWidth
        maxWidth='md'
        aria-labelledby='dialog-title'
      >
        <DialogTitle id='dialog-title' onClose={onClose}>
          {title}
        </DialogTitle>
        <Formik
          enableReinitialize
          initialValues={initialFormValues}
          onSubmit={onSubmit}
          validationSchema={Yup.object().shape({
            recipeName: Yup.string().trim().required('Recipe name is required'),
          })}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            submitForm,
            isSubmitting,
          }) => (
            <>
              <DialogContent dividers>
                <Grid container spacing={3}>
                  <Grid item xs={6}>
                    <Autocomplete
                      id='combo-box-demo'
                      // options={['Akash','Somename','Something']}
                      getOptionLabel={(option: any) => option.title}
                      style={{ width: 300 }}
                      renderInput={(params: any) => (
                        <TextField
                          {...params}
                          label='Combo box'
                          variant='outlined'
                        />
                      )}
                    />
                  </Grid>
                  <Grid xs={6}></Grid>

                  {/* <Grid item xs={1}>
                    <Paper
                      elevation={0}
                      component='div'
                      className={classes.addIngredients}
                    >
                      <Typography
                        className={classes.lColor}
                        align={'center'}
                        variant={'h6'}
                      >
                        3
                      </Typography>
                    </Paper>
                  </Grid> */}
                </Grid>
              </DialogContent>

              <DialogActions>
                <Button onClick={onClose} variant='outlined' color='secondary'>
                  Cancel
                </Button>

                <Button
                  className={classes.themeButton}
                  onClick={() => submitForm()}
                  disabled={isSubmitting}
                  variant='outlined'
                >
                  {isSubmitting ? (
                    <CircularProgress size={24} className={classes.lColor} />
                  ) : (
                    okBtnText
                  )}
                </Button>
              </DialogActions>
            </>
          )}
        </Formik>
      </Dialog>
    </div>
  );
};

export default SkinCarePlan;
