import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import moment from 'moment';
import { makeStyles } from '@material-ui/styles';
import {
  Card,
  CardHeader,
  CardContent,
  CardActionArea,
  CardMedia,
  Avatar,
  Link,
  Typography,
  Divider,
  Input,
  Paper,
  Tooltip,
  IconButton
} from '@material-ui/core';
import AccessTimeIcon from '@material-ui/icons/AccessTime';
import SendIcon from '@material-ui/icons/Send';

// import { Reactions, CommentBubble, CommentForm } from './components';

const useStyles = makeStyles((theme: any) => ({
  root: {},
  subheader: {
    display: 'flex',
    alignItems: 'center'
  },
  accessTimeIcon: {
    color: theme.palette.text.secondary,
    fontSize: '14px',
    height: 14,
    width: 14,
    marginRight: 6
  },
  content: {
    paddingTop: 0
  },
  message: {
    marginBottom: theme.spacing(2)
  },
  mediaArea: {
    marginBottom: theme.spacing(2)
  },
  media: {
    height: 400,
    backgroundPosition: 'initial'
  },
  divider: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2)
  },
  replayInsertDiv: {
    display: 'flex',
    alignItems: 'center'
  },
  paper: {
    flexGrow: 1,
    marginLeft: theme.spacing(2),
    padding: theme.spacing(0.5, 2)
  },
  input: {
    width: '100%'
  },
  replayListDiv: {
    display: 'flex',
    marginBottom: theme.spacing(2)
  },
  bubble: {
    flexGrow: 1,
    padding: theme.spacing(1),
    marginLeft: theme.spacing(2),
    borderRadius: theme.shape.borderRadius,
    backgroundColor: theme.palette.background.default
  },
  header: {
    display: 'flex',
    alignItems: 'center'
  },
  time: {
    marginLeft: 'auto'
  },
  replayMessage: {
    marginTop: theme.spacing(1)
  }

}));

const AppUserFeedback = (props: any) => {
  const classes = useStyles();

  return (
    <Card>
      <CardHeader
        avatar={
          <Avatar
            alt="Person"
            src={''}

          />
        }
        disableTypography
        subheader={
          <div className={classes.subheader}>
            <AccessTimeIcon className={classes.accessTimeIcon} />
            <Typography variant="body2">
              24 hours
            </Typography>
          </div>
        }
        title={'Jobins Raj'}
      />
      <CardContent className={classes.content}>
        <Typography
          className={classes.message}
          variant="body1"
        >
          Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area Message Area
        </Typography>


        <Divider className={classes.divider} />
        <div className={classes.replayListDiv}
        >
          <Avatar
            alt="Jobins Raj"

            src={''}

          />
          <div className={classes.bubble}>
            <div className={classes.header}>
              <Link
                color="textPrimary"
                component={RouterLink}
                to="/profile/1/timeline"
                variant="h6"
              >
                Jobins Raj
              </Link>
              <Typography
                className={classes.time}
                variant="body2"
              >
                3 h
              </Typography>
            </div>
            <Typography
              className={classes.replayMessage}
              variant="body1"
            >
              Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message Replay Message
            </Typography>
          </div>
        </div>

        <Divider className={classes.divider} />


        <div className={classes.replayInsertDiv} >
          <Avatar
            alt="Person"
            src={''}
          />{' '}
          <Paper
            className={classes.paper}
            elevation={1}
          >
            <Input
              className={classes.input}
              disableUnderline
              // onChange={handleChange}
              placeholder="Leave a message"
            />
          </Paper>
          <Tooltip title="Send">
            <IconButton color='primary'>
              <SendIcon />
            </IconButton>
          </Tooltip>

        </div>


      </CardContent>
    </Card>
  );
};

export default AppUserFeedback;
