import React from 'react';
import {
  makeStyles,
  Grid,
  Typography,
  Paper,
  Dialog,
  DialogActions,
  DialogContent,
  Button,
} from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';
import Page from '../../components/Page/Page';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { Formik } from 'formik';
import * as Yup from 'yup';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  themeButton: {
    color: theme.palette.white,
    backgroundColor: theme.palette.green.main,
    '&:hover': {
      backgroundColor: theme.palette.green.dark,
    },
  },
}));

function SkinCarePlan() {
  const classes = useStyles();
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Skin Care Plan',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const closeAddEditDialog = () => {
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  return (
    <div className={classes.root}>
      <Page title='Skin Care Recipe' />

      {/* =======Header====== */}
      <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
        <Grid item>
          <Typography component='h1' variant='h3'>
            Skin Care Plan
          </Typography>
        </Grid>
        <Grid item>
          <Button
            variant='contained'
            onClick={() => openAddDialog()}
            className={classes.themeButton}
            startIcon={<AddIcon />}
          >
            Add
          </Button>
        </Grid>
      </Grid>

      {/* ==========Add & Edit Dialoge======== */}

      <AddEditDialog {...addEditDialog} onClose={closeAddEditDialog} />
    </div>
  );
}

const AddEditDialog = (props: any) => {
  const { isEdit, isOpen, okBtnText = 'OK', onClose, title } = props;
  const initialFormValues = {
    skinType: '',
    skinIssues: '',
    recipe: '',
  };

  const onSubmit = () => {
    alert('submmitting');
  };

  return (
    <div>
      <Dialog
        open={isOpen}
        disableBackdropClick
        disableEscapeKeyDown
        fullWidth
        maxWidth='md'
        aria-labelledby='dialog-title'
      >
        <DialogTitle id='dialog-title' onClose={onClose}>
          {title}
        </DialogTitle>
        <Formik
          enableReinitialize
          initialValues={initialFormValues}
          onSubmit={onSubmit}
          validationSchema={Yup.object().shape({
            recipeName: Yup.string().trim().required('Recipe name is required'),
            skinType: Yup.string().trim().required('Skin type is required'),
            ingredients: Yup.array().required('Ingredients is required'),
            image: Yup.object({
              file: Yup.mixed().required('A file is required'),
            }),
          })}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            submitForm,
            isSubmitting,
          }) => (
            <div>
              <h1>Header</h1>
            </div>
          )}
        </Formik>
      </Dialog>
    </div>
  );
};

export default SkinCarePlan;
