import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import AddIcon from '@material-ui/icons/Add';
import CustomModel from '../../components/Model/Model';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  addIcon: {
    marginRight: theme.spacing(1),
  },
  header: {
    marginBottom: theme.spacing(3),
    display: 'flex',
    justifyContent: 'space-between',
  },
  addcategoriesStyle: {
    backgroundColor: theme.palette.green.main,
    cursor: 'pointer',
    color: 'white',
    '&:hover': {
      backgroundColor: theme.palette.green.main,
      color: 'white',
    },
  },
  SearchStyle: {
    width: '50%',
  },
  modelBody: {
    paddingTop: theme.spacing(2),
  },
  recipetextfieldStyle: {
    width: '100%',
  },
  textareastyle: {
    borderRadius: 5,
    width: '100%',
    height: '100px !important',
    paddingLeft: 4,
    paddingTop: 10,
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    outline: 'none',
  },
}));

function Skincare() {
  const classes = useStyles();
  const [searchItem, setSearchItem] = React.useState('');
  const [recipeName, setRecipeName] = React.useState('');
  const [viewAndEditModel, setViewAndEditModel] = React.useState(false);
  return (
    <div className={classes.root}>
      <div className={classes.header}>
        <TextField
          id='outlined-basic'
          label='Search'
          variant='outlined'
          value={searchItem}
          onChange={(e) => setSearchItem(e.target.value)}
          className={classes.SearchStyle}
        />
        <Button
          onClick={() => setViewAndEditModel(true)}
          variant='contained'
          className={classes.addcategoriesStyle}
        >
          <AddIcon className={classes.addIcon} />
          Add Items
        </Button>
      </div>
      <div>
        <CustomModel
          title='Add Skin Care Item'
          open={viewAndEditModel}
          onCancel={() => setViewAndEditModel(false)}
        >
          <div className={classes.modelBody}>
            <div>
              <TextField
                id='outlined-basic'
                label='Recipe Name'
                value={recipeName}
                onChange={(e) => setRecipeName(e.target.value)}
                className={classes.recipetextfieldStyle}
              />
              <TextareaAutosize
                className={classes.textareastyle}
                rowsMin={3}
                placeholder='Description'
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>
          </div>
        </CustomModel>
      </div>
    </div>
  );
}

export default Skincare;
