import React from 'react';
import { Button, Card, CardActionArea, CardActions, Avatar, CardContent, CardMedia, CircularProgress, Dialog, DialogActions, DialogContent, Divider, FormControl, FormHelperText, Grid, IconButton, makeStyles, Paper, Tab, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TextField, Tooltip, Typography } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { Formik } from 'formik';
import * as Yup from 'yup';

const useStyles = makeStyles((theme: any)=>({
    root : {
        width : '100%',
        height : '91vh'
    },
    displayFlex : {
        padding :theme.spacing(10),
        display : 'flex',
        justifyContent : 'center',
        alignItems : 'center',
        height : '100%'
    },
    spaceEvenly : {
        display : 'flex',
        flexDirection : 'column',
        justifyContent : 'space-evenly',
        alignItems : 'center',
        height : '100%',
        width : '80%',
        margin:'auto'
    },
    paperStyle : {
        width : '100%',
        height : '100%'
    },
    avatharStyle : {
        width : theme.spacing(20),
        height : theme.spacing(20)
    },
    themeButton: {
        color: theme.palette.white,
        backgroundColor: theme.palette.green.main,
        '&:hover': {
          backgroundColor: theme.palette.green.dark,
        },
    },
}))

function AdminProfileDetails() {
    const classes = useStyles()
    return (
        <div className={classes.root}>
            <div className={classes.displayFlex}>
                <Paper className = {classes.paperStyle}>
                    <div className={classes.spaceEvenly}>
                        <Avatar variant='square' className={classes.avatharStyle} />
                        <Table style={{width : '65%'}}>
                            <TableBody>
                            <TableRow >
                                <TableCell align = 'right'>Name</TableCell>
                                <TableCell align = 'right'>SomeName</TableCell>
                                <TableCell align = 'right'><IconButton className={classes.themeButton}><EditIcon /></IconButton></TableCell>
                            </TableRow>
                            <TableRow >
                                <TableCell align = 'right'>Email</TableCell>
                                <TableCell align = 'right'>SomeName@123.com</TableCell>
                                <TableCell align = 'right'></TableCell>
                            </TableRow>
                            <TableRow >
                                <TableCell align = 'right'>Admin Type</TableCell>
                                <TableCell align = 'right'>Nutritionist</TableCell>
                                <TableCell align = 'right'></TableCell>
                            </TableRow>
                            <TableRow >
                                <TableCell align = 'right'>Password</TableCell>
                                <TableCell align = 'right'>**********</TableCell>
                                <TableCell align = 'right'><ChangePassword /></TableCell>
                            </TableRow>
                            </TableBody>
                        </Table>
                    </div>
                </Paper>
            </div>
        </div>
    )
}

const initialValues = {
    password : '',
    confirmPassword : '',
}
export const ChangePassword = () =>{
    const classes = useStyles();
    const formikRef = React.useRef<any>(null);
    const [isOpen, setIsOpen] = React.useState(false);
    const [initialFormValues, setInitialFormValues] = React.useState({...initialValues})

    const changePasswordModel = () =>{
        setIsOpen(!isOpen)
    }

    const onClose = () =>{
        setIsOpen(!isOpen)
    }

    const onSubmit = () =>{
        alert('Done')
    }

    return(
        <>
            <Button onClick={changePasswordModel} className={classes.themeButton}>ChangePassword</Button>
            <Dialog
                open={isOpen}
                disableBackdropClick
                disableEscapeKeyDown
                fullWidth
                maxWidth='sm'
                aria-labelledby='dialog-title'
            >
                <DialogTitle id='dialog-title' onClose={onClose}>
                    Change Password
                </DialogTitle>
                <Formik
                    innerRef={formikRef}
                    enableReinitialize
                    initialValues={initialFormValues}
                    validationSchema={Yup.object().shape({
                        password : Yup.string().required('Password is required'),
                        confirmPassword : Yup.string().oneOf([Yup.ref('password'),null], 'Password must be same').required('Confirm password is required')
                    })}
                    onSubmit={onSubmit}
                >
                {({ values, errors, touched, handleChange, handleBlur, setFieldValue, submitForm, isSubmitting }) => (
                    <>
                    <DialogContent dividers>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField 
                                    fullWidth 
                                    label = 'Password'
                                    variant='outlined' 
                                    name = 'password'
                                    type = 'password'
                                    value={values.password}
                                    onBlur={handleBlur}
                                    onChange={handleChange}
                                    error={Boolean(touched.password && errors.password)}
                                    helperText={touched.password && errors.password}
                                />    
                            </Grid>
                            <Grid item xs={12}>
                                <TextField 
                                    fullWidth 
                                    label = 'Confirm Password'
                                    variant='outlined'
                                    name = 'confirmPassword'
                                    type = 'password'
                                    value={values.confirmPassword}
                                    onBlur={handleBlur}
                                    onChange={handleChange}
                                    error={Boolean(touched.confirmPassword && errors.confirmPassword)}
                                    helperText={touched.confirmPassword && errors.confirmPassword}
                                />   
                            </Grid>
                        </Grid>
                    </DialogContent>

                    <DialogActions>
                        <Button onClick={onClose} variant='outlined' color='secondary'>
                        Cancel
                        </Button>
                        <Button
                        className={classes.themeButton}
                        onClick={() => submitForm()}
                        disabled={isSubmitting}
                        variant='outlined'
                        >
                        {isSubmitting ? (
                            <CircularProgress size={24} style={{ color: 'white' }} />
                        ) : (
                            'ChangePassword'
                        )}
                        </Button>
                    </DialogActions>
                    </>
                )}
                </Formik>
            </Dialog>
        </>
    )
}


export default AdminProfileDetails
