export const unsetAllMarks: RawCommands['unsetAllMarks'] = () => ({ tr, state, dispatch }) => {
  const { selection } = tr
  const { empty, ranges } = selection

  if (empty) {
    return true
  }

  if (dispatch) {
    Object
      .entries(state.schema.marks)
      .forEach(([, mark]) => {
        ranges.forEach(range => {
          tr.removeMark(range.$from.pos, range.$to.pos, mark as any)
        })
      })
  }

  return true
}
