import { Node as ProseMirrorNode } from 'prosemirror-model'
import { JSONContent } from '../types'

interface DebugJSONContent extends JSONContent {
  from: number,
  to: number,
}

/**
 * Returns a node tree with node positions.
 */
export default function getDebugJSON(node: ProseMirrorNode, startOffset = 0) {
  const nodes: DebugJSONContent[] = []

  node.forEach((n, offset) => {
    const from = startOffset + offset
    const to = from + n.nodeSize
    const marks = n.marks.map(mark => ({
      type: mark.type.name,
      attrs: { ...mark.attrs },
    }))
    const attrs = { ...n.attrs }
    const content = getDebugJSON(n, from + 1)
    const output: DebugJSONContent = {
      type: n.type.name,
      from,
      to,
    }

    if (Object.keys(attrs).length) {
      output.attrs = attrs
    }

    if (marks.length) {
      output.marks = marks
    }

    if (content.length) {
      output.content = content
    }

    if (n.text) {
      output.text = n.text
    }

    nodes.push(output)
  })

  return nodes
}
import { NodeSelection } from 'prosemirror-state'
import isObject from '../utilities/isObject'

export default function isNodeSelection(value: unknown): value is NodeSelection {
  return isObject(value) && value instanceof NodeSelection
}
import { EditorView } from 'prosemirror-view'

export default function posToDOMRect(view: EditorView, from: number, to: number): DOMRect {
  const start = view.coordsAtPos(from)
  const end = view.coordsAtPos(to, -1)
  const top = Math.min(start.top, end.top)
  const bottom = Math.max(start.bottom, end.bottom)
  const left = Math.min(start.left, end.left)
  const right = Math.max(start.right, end.right)
  const width = right - left
  const height = bottom - top
  const x = left
  const y = top
  const data = {
    top,
    bottom,
    left,
    right,
    width,
    height,
    x,
    y,
  }

  return {
    ...data,
    toJSON: () => data,
  }
}
