import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import SearchBar from '../../components/Common/SearchBar/Searchbar';
import CustomModel from '../../components/Common/DialogBox/DialogBox';
import Typography from '@material-ui/core/Typography';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import TextField from '@material-ui/core/TextField';
import AddIcon from '@material-ui/icons/Add';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Customtable from '../../components/CustomTable/CustomTable';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(2),
  },
  textFieldStyle: {
    width: '100%',
    marginBottom: theme.spacing(1),
    border: '1px solid #c5c3c3',
    borderRadius: 4,
  },
  textAreaStyle: {
    borderRadius: 5,
    width: '100%',
    height: '100px !important',
    paddingLeft: 4,
    paddingTop: 10,
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    outline: 'none',
    border: '1px solid #c5c3c3',
    marginBottom: theme.spacing(1),
  },
  skinItems: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: theme.spacing(0.5),
  },
  skinItemstextfield: {
    width: '48%',
    marginBottom: theme.spacing(1),
    border: '1px solid #c5c3c3',
    borderRadius: 4,
  },
  SelectdropDownStyle: {
    width: '48%',
    marginBottom: theme.spacing(1),
  },
  quantityStyle: {
    width: '70%',
    marginBottom: theme.spacing(1),
    border: '1px solid #c5c3c3',
    borderRadius: 4,
  },
  underline: {
    border: 'none',
  },
  addIngredientsStyle: {
    width: '100%',
    height: 55,
    cursor: 'pointer',
    border: '1px solid #c5c3c3',
    borderRadius: 4,
    padding: theme.spacing(2),
    marginBottom: theme.spacing(1),
  },
}));

const tableHeader = [
  'S.No',
  'Recipe Name',
  'Recipe Image',
  'Recipe Description',
  'Actions',
];

function SkinCare() {
  const classes = useStyles();
  const [openDropDown, setOpenDropDown] = React.useState(false);
  const [addModelOpen, setAddModelOpen] = React.useState(false);
  const [ingredientsData, setIngredientsData] = React.useState<any>([]);
  const [addIngredientsData, setAddIngredientsData] = React.useState([
    {
      ingredients: null,
    },
  ]);

  const handleChange = (event: React.ChangeEvent<{ value: unknown }>) => {
    setIngredientsData(event.target.value as string);
  };

  const openIngredientsDropDown = () => {
    let addAnotherField = [...addIngredientsData];
    addAnotherField.push({ ingredients: null });
    setAddIngredientsData(addAnotherField);
  };

  console.log(addIngredientsData);
  return (
    <div className={classes.root}>
      <SearchBar modelOpen={() => setAddModelOpen(!addModelOpen)} />
      <CustomModel
        open={addModelOpen}
        close={() => setAddModelOpen(!addModelOpen)}
        Title=' Add Skin Care Items'
      >
        <Grid container spacing={4}>
          <Grid xs={12}>
            <TextField
              className={classes.textFieldStyle}
              margin='none'
              placeholder='Enter Recipe Name'
              variant='outlined'
              InputProps={{
                classes: { notchedOutline: classes.underline },
              }}
            />
          </Grid>
          <Grid xs={12}>
            <TextareaAutosize
              className={classes.textAreaStyle}
              aria-label='minimum height'
              rowsMin={3}
              placeholder='Recipe Description'
            />
          </Grid>
        </Grid>

        <div className={classes.skinItems}>
          <TextField
            className={classes.skinItemstextfield}
            placeholder='Enter Skin Type'
            variant='outlined'
            InputProps={{
              classes: { notchedOutline: classes.underline },
            }}
          />
          <TextField
            className={classes.skinItemstextfield}
            placeholder='Enter Skin Issue'
            variant='outlined'
            InputProps={{
              classes: { notchedOutline: classes.underline },
            }}
          />
        </div>
        <div
          onClick={openIngredientsDropDown}
          className={classes.addIngredientsStyle}
        >
          <div className={classes.skinItems}>
            <Typography>Add Ingredients</Typography>
            <AddIcon />
          </div>
        </div>
        {addIngredientsData.map((item: any, index: any) => {
          return (
            <div key={index} className={classes.skinItems}>
              <FormControl
                variant='outlined'
                className={classes.SelectdropDownStyle}
              >
                <InputLabel htmlFor='outlined-age-native-simple'>
                  Ingredients
                </InputLabel>
                <Select
                  label='Ingredients'
                  native
                  id='outlined-age-native-simple'
                  onChange={handleChange}
                >
                  <option aria-label='None' value='' />
                  <option value={10}>Peanuts</option>
                  <option value={20}>Almonds</option>
                  <option value={30}>Walnuts</option>
                </Select>
              </FormControl>
              <TextField
                className={classes.skinItemstextfield}
                placeholder='Quantity'
                variant='outlined'
                InputProps={{
                  classes: { notchedOutline: classes.underline },
                }}
              />
            </div>
          );
        })}

        <TextField
          className={classes.textFieldStyle}
          margin='none'
          placeholder='Enter Recipe Name'
          variant='outlined'
          InputProps={{
            classes: { notchedOutline: classes.underline },
          }}
        />
        <TextareaAutosize
          className={classes.textAreaStyle}
          aria-label='minimum height'
          rowsMin={3}
          placeholder='Recipe Description'
        />
      </CustomModel>

      {/* ................................................Tabel............................................................... */}
      <Customtable
        tableHeaders={tableHeader}
        tableBodyData={[
          { name: 'name', description: 'discription', image: 'image' },
        ]}
      />
    </div>
  );
}

export default SkinCare;
