import React, { useEffect, useRef, useState } from 'react';
import { Avatar, Button, Card, CardActions, CardContent, CircularProgress, Dialog, DialogActions, DialogContent, Grid, IconButton, makeStyles, Paper, Tab, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TextField, Tooltip, Typography } from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import AddIcon from '@material-ui/icons/Add';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination, TabContext, TabList, TabPanel } from '@material-ui/lab';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { Formik } from 'formik';
import * as Yup from 'yup';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import Page from '../../components/Page/Page';
import useConfModel from '../../hook/useConfModel';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';
import ControlPointIcon from '@material-ui/icons/ControlPoint';


const useStyles = makeStyles((theme: any) => ({
    root: {
      width: theme.breakpoints.values.lg,
      maxWidth: '100%',
      margin: '0 auto',
      padding: theme.spacing(3),
    },
    themeButton: {
        color: theme.palette.white,
        backgroundColor: theme.palette.green.main,
        '&:hover': {
          backgroundColor: theme.palette.green.dark,
        },
      },
  }));

function NutritionIngredients() {
  const classes = useStyles()
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Nutrition Ingredients',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const closeAddEditDialog = () =>{
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  }

  return (
    <div className={classes.root}>
      <Page title='Nutrition Ingredients' />
      <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
        <Grid item>
          <Typography component='h1' variant='h3'>
            Nutrition Ingredients
          </Typography>
        </Grid>
        <Grid item>
          <Button
            variant='contained'
            onClick={() => openAddDialog()}
            className={classes.themeButton}
            startIcon={<AddIcon />}
          >
            Add
          </Button>
        </Grid>
      </Grid>
      <ViewandEditDialogue {...addEditDialog}  onClose={closeAddEditDialog}/>
    </div>
  );
}

const ViewandEditDialogue = (props: any) =>{
    const { isEdit, isOpen, okBtnText = 'OK', onClose,data, title, onSuccess} = props;
    const classes = useStyles()
    const formikRef = useRef<any>(null)
    const initialValues = {
        nutrientName : '',
        image : '',
        quantity : '',
        protien : '',
        fat : '',
        carbs : '',
        calories : '',
        benifits : [],
        description : ''
    };
    const [initialFormValues,setInitialFormValues] = React.useState({...initialValues});

    const onSubmit = () =>{
        alert('done')
    }

    return (
    <div>
       <Dialog 
        open={isOpen}
        disableBackdropClick
        disableEscapeKeyDown
        fullWidth
        maxWidth='md'
        aria-labelledby='dialog-title' >

        <DialogTitle id='dialog-title' onClose={onClose}>
          {title}
        </DialogTitle>
        <Formik
        innerRef={formikRef}
        enableReinitialize
        initialValues={initialFormValues}
        onSubmit={onSubmit}
        validationSchema={Yup.object().shape({
            nutrientName: Yup.string().trim().required('Name is required'),
            quantity : Yup.string().trim().required('Quality is required'),
        })}
        >
            {({values, errors, touched, handleBlur,handleChange, submitForm, isSubmitting,})=>(
                <>
                <DialogContent dividers>
                    <Grid container spacing = {2}>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                label='Name'
                                name='nutrientName'
                                variant='outlined'
                                error={Boolean(touched.nutrientName && errors.nutrientName)}
                                helperText={touched.nutrientName && errors.nutrientName}
                                value={values.nutrientName}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                label='Quantity'
                                name='quantity'
                                variant='outlined'
                                error={Boolean(touched.nutrientName && errors.nutrientName)}
                                helperText={touched.nutrientName && errors.nutrientName}
                                value={values.quantity}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Protien'
                                name='recipe_name'
                                variant='outlined'
                                error={Boolean(touched.nutrientName && errors.nutrientName)}
                                helperText={touched.nutrientName && errors.nutrientName}
                                value={values.quantity}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Fat'
                                name='recipe_name'
                                variant='outlined'
                                error={Boolean(touched.nutrientName && errors.nutrientName)}
                                helperText={touched.nutrientName && errors.nutrientName}
                                value={values.quantity}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Carbs'
                                name='recipe_name'
                                variant='outlined'
                                error={Boolean(touched.nutrientName && errors.nutrientName)}
                                helperText={touched.nutrientName && errors.nutrientName}
                                value={values.quantity}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Calories'
                                name='recipe_name'
                                variant='outlined'
                                error={Boolean(touched.nutrientName && errors.nutrientName)}
                                helperText={touched.nutrientName && errors.nutrientName}
                                value={values.quantity}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Button
                          fullWidth
                          className={classes.themeButton}
                          variant='contained'
                          color='default'
                          // onClick={() => addIngredients(values, setFieldValue)}
                          endIcon={<ControlPointIcon />}
                        >
                    Add Ingredients
                  </Button>

                    </Grid>
                </DialogContent>
                </>
            )}
        </Formik>

       </Dialog>
    </div>
    )
}

export default NutritionIngredients;
