import React from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import firebase from '../../firebase';
import { makeStyles } from '@material-ui/core/styles';
import { LoginApi } from '../../Services/Api';
import { useNavigate } from 'react-router-dom';
import { DashboardRoute, IngredientsRoute } from '../../Routes/RoutesConstants';

const useStyles = makeStyles((theme) => ({
  loginStyle: {
    width: '80%',
    backgroundColor: 'white',
    margin: 'auto',
    padding: theme.spacing(2),
    marginTop: theme.spacing(22),
    borderRadius: '10px',
    boxShadow: ' 0px 20px 40px #0000001F',
    [theme.breakpoints.down('md')]: {
      width: '90%',
    },
  },
  SigninTextStyle: {
    textAlign: 'start',
    paddingLeft: theme.spacing(3),
    paddingBottom: theme.spacing(2),
    paddingTop: theme.spacing(1),
    fontWeight: 500,
    color: '#41A58D',
  },
  textboxStyle: {
    paddingTop: theme.spacing(1),
  },
  textboxStyle1: {
    paddingTop: theme.spacing(3),
  },
  textfieldStyle: {
    width: '90%',
  },
  forgotpasswordStyle: {
    paddingTop: theme.spacing(3),
    paddingRight: theme.spacing(3),
    textAlign: 'end',
    textDecoration: 'underline',
    cursor: 'pointer',
  },
  buttonStyle: {
    display: 'flex',
    flex: 1,
    justifyContent: 'space-between',
    padding: theme.spacing(3.5),
  },
  signUpbutton: {
    width: '40%',
    backgroundColor: 'white',
    border: '2px solid #41A58D',
    color: '#41A58D',
    textTransform: 'capitalize',
    '&:hover': {
      backgroundColor: '#41A58D',
      color: 'white',
    },
  },
  signinButton: {
    width: '40%',
    backgroundColor: '#41A58D',
    color: 'white',
    textTransform: 'capitalize',
    '&:hover': {
      backgroundColor: 'white',
      color: '#41A58D',
      border: '2px solid #41A58D',
    },
  },
  textfields: {
    paddingLeft: theme.spacing(3),
  },
}));

function SignIn(props: any) {
  const classes = useStyles();
  const navigate = useNavigate();
  const [userEmail, setUserEmail] = React.useState('');
  const [userPassword, setUserPassowrd] = React.useState('');
  const [isLoading, setIsLoading] = React.useState(false);

  const loginUser = () => {
    setIsLoading(true);
    firebase
      .auth()
      .signInWithEmailAndPassword(userEmail, userPassword)
      .then((response: any) => {
        localStorage.setItem('uid', response.user.uid);
      })
      .then(() => {
        LoginApi({}, onSuccessLogin);
      })
      .catch((error) => {
        throw error;
      });
  };

  const onSuccessLogin = (response: any) => {
    setIsLoading(false);
    navigate(IngredientsRoute);
  };

  return (
    <div>
      <Grid container>
        <Grid item xs={12} md={12} lg={3}></Grid>
        <Grid item xs={12} md={12} lg={6}>
          <div className={classes.loginStyle}>
            <div>
              <Typography variant='h5' className={classes.SigninTextStyle}>
                SignIn
              </Typography>
              <Divider className={classes.divider} />
              <div className={classes.textfields}>
                <div className={classes.textboxStyle}>
                  <TextField
                    className={classes.textfieldStyle}
                    value={userEmail}
                    onChange={(e) => setUserEmail(e.target.value)}
                    label='Enter Email'
                  />
                </div>
                <div className={classes.textboxStyle1}>
                  <TextField
                    type='password'
                    value={userPassword}
                    onChange={(e) => setUserPassowrd(e.target.value)}
                    className={classes.textfieldStyle}
                    label='Enter Password'
                  />
                </div>
              </div>
              <Typography className={classes.forgotpasswordStyle}>
                Forgot Password ?
              </Typography>
              <div className={classes.buttonStyle}>
                <Button className={classes.signUpbutton}>SignUp</Button>
                <Button onClick={loginUser} className={classes.signinButton}>
                  {isLoading ? 'Signing In' : 'Sign In'}
                  {isLoading ? (
                    <i
                      style={{ fontSize: 20, marginLeft: 20 }}
                      className='fas fa-spinner fa-pulse'
                    ></i>
                  ) : (
                    ''
                  )}
                </Button>
              </div>
            </div>
          </div>
        </Grid>
        <Grid item xs={12} md={12} lg={3}></Grid>
      </Grid>
    </div>
  );
}

export default SignIn;
