import React from 'react'
import { Grid, Button, Paper, makeStyles, Avatar} from '@material-ui/core'
import Page from '../../components/Page/Page';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';


const useStyle = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    height : '100%',
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  paperStyle : {
      width : '100%',
      height : '85vh'
  }

}));

const AdminProfileDetails = () => {
  const classes = useStyle();
  const { Post } = useService();
  const Snackbar = useSnackbar();

  return (
    <div className={classes.root}>
      <Page title='Admin Profile' />
        <Grid alignItems = 'center' container spacing={2}>
            <Grid item xs={12} md={2}></Grid>
            <Grid item xs={12} md={8}>
                <Paper className={classes.paperStyle}>
                    {/* <div style={{display: 'flex',justifyContent : 'center',flexDirection : 'column',alignItems : 'center',height : '100%'}}> */}
                        <Avatar style={{width : '150px',height : '150px'}} variant='square' />
                    {/* </div> */}
                </Paper>
            </Grid>
            <Grid item xs={12} md={2}></Grid>
        </Grid>

    </div>
  )
}

export default AdminProfileDetails;
