import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import AddIcon from '@material-ui/icons/Add';
import CustomModel from '../../components/Model/Model';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import CustomuploadButton from '../../components/Buttons/UploadButton';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  addIcon: {
    marginRight: theme.spacing(1),
  },
  header: {
    marginBottom: theme.spacing(3),
    display: 'flex',
    justifyContent: 'space-between',
  },
  addcategoriesStyle: {
    backgroundColor: theme.palette.green.main,
    cursor: 'pointer',
    color: 'white',
    '&:hover': {
      backgroundColor: theme.palette.green.main,
      color: 'white',
    },
  },
  SearchStyle: {
    width: '50%',
  },
  modelBody: {
    width: '48%',
    paddingTop: theme.spacing(1),
    [theme.breakpoints.down('sm')]: {
      width: '100%',
    },
  },
  recipetextfieldStyle: {
    width: '45%',
  },
  textareastyle: {
    borderRadius: 5,
    width: '100%',
    height: '100px !important',
    paddingLeft: 4,
    paddingTop: 10,
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    outline: 'none',
    marginTop: 20,
  },
  ImageBody: {
    marginTop: theme.spacing(1),
    width: '48%',
    height: 300,
    [theme.breakpoints.down('sm')]: {
      width: '100%',
    },
  },
  imagesStyle: {
    maxHeight: '100%',
    maxWidth: '100%',
  },
  MainModelBody: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    flexDirection: 'row',
    [theme.breakpoints.down('sm')]: {
      flexDirection: 'column',
    },
  },
  textFieldsStyles: {
    display: 'flex',
    justifyContent: 'space-between',
    paddingTop: theme.spacing(2),
  },
}));

function Skincare() {
  const classes = useStyles();
  const [searchItem, setSearchItem] = React.useState('');
  const [recipeName, setRecipeName] = React.useState('');
  const [recipeDescription, setRecipeDescription] = React.useState('');
  const [viewAndEditModel, setViewAndEditModel] = React.useState(false);
  return (
    <div className={classes.root}>
      <div className={classes.header}>
        <TextField
          id='outlined-basic'
          label='Search'
          variant='outlined'
          value={searchItem}
          onChange={(e) => setSearchItem(e.target.value)}
          className={classes.SearchStyle}
        />
        <Button
          onClick={() => setViewAndEditModel(true)}
          variant='contained'
          className={classes.addcategoriesStyle}
        >
          <AddIcon className={classes.addIcon} />
          Add Items
        </Button>
      </div>
      <div>
        <CustomModel
          width100
          title='Add Skin Care Item'
          open={viewAndEditModel}
          onCancel={() => setViewAndEditModel(false)}
        >
          <div className={classes.MainModelBody}>
            <div className={classes.modelBody}>
              <div className={classes.textFieldsStyles}>
                <TextField
                  id='outlined-basic'
                  label='Recipe Name'
                  value={recipeName}
                  onChange={(e) => setRecipeName(e.target.value)}
                  className={classes.recipetextfieldStyle}
                />
                <TextField
                  id='outlined-basic'
                  label='Skin Type'
                  value={recipeName}
                  onChange={(e) => setRecipeName(e.target.value)}
                  className={classes.recipetextfieldStyle}
                />
              </div>
              <div className={classes.textFieldsStyles}>
                <TextField
                  id='outlined-basic'
                  label='Skin Issue'
                  value={recipeName}
                  onChange={(e) => setRecipeName(e.target.value)}
                  className={classes.recipetextfieldStyle}
                />
                <TextField
                  id='outlined-basic'
                  label='Preparation timing'
                  value={recipeName}
                  onChange={(e) => setRecipeName(e.target.value)}
                  className={classes.recipetextfieldStyle}
                />
              </div>
              <TextareaAutosize
                className={classes.textareastyle}
                rowsMin={3}
                placeholder='Recipe Description'
                value={recipeDescription}
                onChange={(e) => setRecipeDescription(e.target.value)}
              />
              <TextareaAutosize
                className={classes.textareastyle}
                rowsMin={3}
                placeholder='Preparation Description'
                value={recipeDescription}
                onChange={(e) => setRecipeDescription(e.target.value)}
              />
            </div>
            <div className={classes.ImageBody}>
              <img
                src='http://lh3.googleusercontent.com/SHBh5jaK8TH0VchLanPRNH_HRDFPVBHRaQAzP6meOc2H5UMJrUBryjNNU0c3IAT12q05QLmyGzKSMKUt2lgyZmxKS7qAlkw3rZmYmpVv_g=s650'
                alt='some Name'
                className={classes.imagesStyle}
              />
              <CustomuploadButton
                style={{
                  display: 'flex',
                  justifyContent: 'center',
                  paddingTop: 20,
                }}
                ButtonName={'Upload Image'}
              />
            </div>
          </div>
        </CustomModel>
      </div>
    </div>
  );
}

export default Skincare;
