import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import AddIcon from '@material-ui/icons/Add';
import CustomModel from '../../components/Model/Model';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  addIcon: {
    marginRight: theme.spacing(1),
  },
  header: {
    marginBottom: theme.spacing(3),
    display: 'flex',
    justifyContent: 'space-between',
  },
  addcategoriesStyle: {
    backgroundColor: theme.palette.green.main,
    cursor: 'pointer',
    color: 'white',
    '&:hover': {
      backgroundColor: theme.palette.green.main,
      color: 'white',
    },
  },
  SearchStyle: {
    width: '50%',
  },
}));

function Skincare() {
  const classes = useStyles();
  const [searchItem, setSearchItem] = React.useState('');
  const [viewAndEditModel, setViewAndEditModel] = React.useState(false);
  return (
    <div className={classes.root}>
      <div className={classes.header}>
        <TextField
          id='outlined-basic'
          label='Search'
          variant='outlined'
          value={searchItem}
          onChange={(e) => setSearchItem(e.target.value)}
          className={classes.SearchStyle}
        />
        <Button
          onClick={() => setViewAndEditModel(true)}
          variant='contained'
          className={classes.addcategoriesStyle}
        >
          <AddIcon className={classes.addIcon} />
          Add Items
        </Button>
      </div>
      <div>
        <CustomModel
          title='Add Skin Care Item'
          open={viewAndEditModel}
          onCancel={() => setViewAndEditModel(false)}
        >
          <div>
            <h1>Header</h1>
          </div>
        </CustomModel>
      </div>
    </div>
  );
}

export default Skincare;
