import React from 'react';
import {
  makeStyles,
  Avatar,
  Card,
  CardActions,
  CardContent,
  InputLabel,
  Dialog,
  DialogActions,
  DialogContent,
  FormControl,
  FormHelperText,
  Grid,
  IconButton,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Select,
} from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';
import Page from '../../components/Page/Page';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { Formik } from 'formik';
import * as Yup from 'yup';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  themeButton: {
    color: theme.palette.white,
    backgroundColor: theme.palette.green.main,
    '&:hover': {
      backgroundColor: theme.palette.green.dark,
    },
  },
  widthAndHeight: {
    width: '100%',
    height: '100%',
  },
}));

function SkinCarePlan() {
  const classes = useStyles();
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Skin Care Plan',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const closeAddEditDialog = () => {
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  return (
    <div className={classes.root}>
      <Page title='Skin Care Recipe' />

      {/* =======Header====== */}
      <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
        <Grid item>
          <Typography component='h1' variant='h3'>
            Skin Care Plan
          </Typography>
        </Grid>
        <Grid item>
          <Button
            variant='contained'
            onClick={() => openAddDialog()}
            className={classes.themeButton}
            startIcon={<AddIcon />}
          >
            Add
          </Button>
        </Grid>
      </Grid>

      {/* ==========Add & Edit Dialoge======== */}

      <AddEditDialog {...addEditDialog} onClose={closeAddEditDialog} />
    </div>
  );
}

const AddEditDialog = (props: any) => {
  const { isEdit, isOpen, okBtnText = 'OK', onClose, title } = props;
  const classes = useStyles();
  const initialFormValues = {
    skinType: '',
    skinIssues: '',
    recipe: '',
  };

  const onSubmit = () => {
    alert('submmitting');
  };

  return (
    <div>
      <Dialog
        open={isOpen}
        disableBackdropClick
        disableEscapeKeyDown
        fullWidth
        maxWidth='md'
        aria-labelledby='dialog-title'
      >
        <DialogTitle id='dialog-title' onClose={onClose}>
          {title}
        </DialogTitle>
        <Formik
          enableReinitialize
          initialValues={initialFormValues}
          onSubmit={onSubmit}
          validationSchema={Yup.object().shape({
            recipeName: Yup.string().trim().required('Recipe name is required'),
          })}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            submitForm,
            isSubmitting,
          }) => (
            <>
              <DialogContent dividers>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <FormControl
                      className={classes.widthAndHeight}
                      variant='outlined'
                    >
                      <InputLabel htmlFor='outlined-age-native-simple'>
                        Skin Type
                      </InputLabel>
                      <Select
                        native
                        label='Skin Type'
                        inputProps={{
                          name: 'ingredients',
                          id: 'outlined-age-native-simple',
                        }}
                      >
                        <option aria-label='None' value='' />
                        <option value={10}>Ten</option>
                        <option value={20}>Twenty</option>
                        <option value={30}>Thirty</option>
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <FormControl
                      className={classes.widthAndHeight}
                      variant='outlined'
                    >
                      <InputLabel htmlFor='outlined-age-native-simple'>
                        Skin Issues
                      </InputLabel>
                      <Select
                        native
                        label='Skin Issues'
                        inputProps={{
                          name: 'ingredients',
                          id: 'outlined-age-native-simple',
                        }}
                      >
                        <option aria-label='None' value='' />
                        <option value={10}>Ten</option>
                        <option value={20}>Twenty</option>
                        <option value={30}>Thirty</option>
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={1}>
                  </Grid>
                  <Grid item xs={11}>
                    <FormControl
                      className={classes.widthAndHeight}
                      variant='outlined'
                    >
                      <InputLabel htmlFor='outlined-age-native-simple'>
                        Skin Issues
                      </InputLabel>
                      <Select
                        native
                        label='Skin Issues'
                        inputProps={{
                          name: 'ingredients',
                          id: 'outlined-age-native-simple',
                        }}
                      >
                        <option aria-label='None' value='' />
                        <option value={10}>Ten</option>
                        <option value={20}>Twenty</option>
                        <option value={30}>Thirty</option>
                      </Select>
                    </FormControl>
                  </Grid>
                </Grid>
              </DialogContent>
            </>
          )}
        </Formik>
      </Dialog>
    </div>
  );
};

export default SkinCarePlan;
