import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import SearchBar from '../../components/Common/SearchBar/Searchbar';
import CustomModel from '../../components/Common/DialogBox/DialogBox';
import Typography from '@material-ui/core/Typography';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import TextField from '@material-ui/core/TextField';
import AddIcon from '@material-ui/icons/Add';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Customtable from '../../components/CustomTable/CustomTable';
import Grid from '@material-ui/core/Grid';
import RemoveCircleOutlineIcon from '@material-ui/icons/RemoveCircleOutline';
import CustomeUploadButton from '../../components/Buttons/UploadButton';
import Button from '@material-ui/core/Button';
import { ListIngredientsApi } from '../../Services/IngredientsAPI/Index';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(2),
  },
  textFieldStyle: {
    width: '100%',
    marginBottom: theme.spacing(1),
    border: '1px solid #c5c3c3',
    borderRadius: 4,
  },
  textAreaStyle: {
    borderRadius: 5,
    width: '100%',
    height: '100px !important',
    paddingLeft: 4,
    paddingTop: 10,
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    outline: 'none',
    border: '1px solid #c5c3c3',
    marginBottom: theme.spacing(1),
  },
  skinItems: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: theme.spacing(0.5),
  },
  skinItemstextfield: {
    // width: '95%',
    marginBottom: theme.spacing(1),
    border: '1px solid #c5c3c3',
    borderRadius: 4,
  },
  SelectdropDownStyle: {
    width: '98%',
    marginBottom: theme.spacing(1),
  },
  quantityStyle: {
    width: '70%',
    marginBottom: theme.spacing(1),
    border: '1px solid #c5c3c3',
    borderRadius: 4,
  },
  underline: {
    border: 'none',
  },
  addIngredientsStyle: {
    width: '100%',
    height: 55,
    cursor: 'pointer',
    border: '1px solid #c5c3c3',
    borderRadius: 4,
    padding: theme.spacing(2),
    marginBottom: theme.spacing(1),
  },
  removeIngredientsStyle: {
    float: 'right',
    marginTop: '-10%',
    marginRight: '-0.5%',
    backgroundColor: 'white',
    color: '#41a58d',
    borderRadius: '40px',
    fontSize: '25px',
    cursor: 'pointer',
    [theme.breakpoint.down('md')]: {
      marginTop: '-25%',
    },
  },
  ImageplaceholderStyle: {
    width: '100%',
    height: '250px',
    display: 'flex',
    justifyContent: 'center',
  },
  AddButton: {
    backgroundColor: theme.palette.green.main,
    color: 'white',
    textTransform: 'capitalize',
  },
}));

const tableHeader = [
  'S.No',
  'Recipe Name',
  'Recipe Image',
  'Recipe Description',
  'Actions',
];

function SkinCare() {
  const classes = useStyles();
  const [addModelOpen, setAddModelOpen] = React.useState(false);
  const [ingredientsQuantity, setIngredientsQuantity] = React.useState('');
  const [getFiles, setGetFiles] = React.useState<any>('');
  const [images, setImages] = React.useState<any>('');
  const [addIngredientsData, setAddIngredientsData] = React.useState<any>([
    {
      ingredients: null,
      quantity: null,
    },
  ]);

  const handleChange = (
    index: any,
    event: React.ChangeEvent<{ value: unknown }>
  ) => {
    const getData = [...addIngredientsData];
    getData[index].ingredients = event.target.value;
    setAddIngredientsData(getData);
  };

  const openIngredientsDropDown = () => {
    let addAnotherField = [...addIngredientsData];
    addAnotherField.push({ ingredients: null, quantity: null });
    setAddIngredientsData(addAnotherField);
  };

  const handleQuantityChange = (
    index: any,
    event: React.ChangeEvent<{ value: unknown }>
  ) => {
    const quantityData = event.target.value;
    const getData = [...addIngredientsData];
    getData[index].quantity = quantityData;
    setAddIngredientsData(getData);
  };

  const deleteIngredientsFunction = (i: any) => {
    const getData = [...addIngredientsData];
    getData.splice(i, 1);
    setAddIngredientsData(getData);
  };

  const handleImageUpload = (event: any) => {
    let reader = new FileReader();
    let file = event.target.files[0];
    reader.onloadend = () => {
      setImages(reader.result);
    };
    reader.readAsDataURL(file);
    setGetFiles(file);
  };

  console.log(addIngredientsData);
  return (
    <div className={classes.root}>
      <SearchBar modelOpen={() => setAddModelOpen(!addModelOpen)} />
      <Customtable
        tableHeaders={tableHeader}
        tableBodyData={[
          { name: 'name', description: 'discription', image: 'image' },
        ]}
      />
      <CustomModel
        open={addModelOpen}
        close={() => setAddModelOpen(!addModelOpen)}
        Title=' Add Skin Care Items'
      >
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              className={classes.textFieldStyle}
              margin='none'
              placeholder='Enter Recipe Name'
              variant='outlined'
              InputProps={{
                classes: { notchedOutline: classes.underline },
              }}
            />
          </Grid>
          <Grid item xs={12}>
            <TextareaAutosize
              className={classes.textAreaStyle}
              aria-label='minimum height'
              rowsMin={3}
              placeholder='Recipe Description'
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              className={classes.skinItemstextfield}
              placeholder='Enter Skin Type'
              variant='outlined'
              InputProps={{
                classes: { notchedOutline: classes.underline },
              }}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              className={classes.skinItemstextfield}
              placeholder='Enter Skin Issue'
              variant='outlined'
              InputProps={{
                classes: { notchedOutline: classes.underline },
              }}
            />
          </Grid>
          <Grid item xs={12}>
            <div className={classes.addIngredientsStyle}>
              <div className={classes.skinItems}>
                <Typography>Add Ingredients</Typography>
                <AddIcon onClick={openIngredientsDropDown} />
              </div>
            </div>
          </Grid>
        </Grid>
        {addIngredientsData.map((item: any, index: any) => {
          return (
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <FormControl
                  variant='outlined'
                  className={classes.SelectdropDownStyle}
                >
                  <InputLabel htmlFor='outlined-age-native-simple'>
                    Ingredients
                  </InputLabel>
                  <Select
                    label='Ingredients'
                    native
                    id='outlined-age-native-simple'
                    onChange={(event) => handleChange(index, event)}
                  >
                    <option aria-label='None' value='' />
                    <option value={10}>Peanuts</option>
                    <option value={20}>Almonds</option>
                    <option value={30}>Walnuts</option>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  value={item.quantity}
                  onChange={(event) => handleQuantityChange(index, event)}
                  className={classes.skinItemstextfield}
                  placeholder='Quantity'
                  variant='outlined'
                  InputProps={{
                    classes: { notchedOutline: classes.underline },
                  }}
                />
              </Grid>
              {addIngredientsData.length > 1 ? (
                <div style={{ width: '100%', zIndex: 100 }}>
                  <RemoveCircleOutlineIcon
                    onClick={() => deleteIngredientsFunction(index)}
                    className={classes.removeIngredientsStyle}
                  />
                </div>
              ) : (
                ''
              )}
            </Grid>
          );
        })}
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              className={classes.textFieldStyle}
              margin='none'
              placeholder='Enter Recipe Name'
              variant='outlined'
              InputProps={{
                classes: { notchedOutline: classes.underline },
              }}
            />
          </Grid>
          <Grid item xs={12}>
            <TextareaAutosize
              className={classes.textAreaStyle}
              aria-label='minimum height'
              rowsMin={3}
              placeholder='Recipe Description'
            />
          </Grid>
          <Grid item xs={12}>
            <CustomeUploadButton
              style={{
                display: 'flex',
                justifyContent: 'center',
              }}
              ButtonName='Recipe Image Upload'
              onChange={handleImageUpload}
            />
          </Grid>
          <Grid item xs={12}>
            <div className={classes.ImageplaceholderStyle}>
              {getFiles ? (
                <img
                  src={images}
                  alt='Skin care recipe image'
                  style={{ maxHeight: '100%', maxWidth: '100%' }}
                />
              ) : (
                ''
              )}
            </div>
          </Grid>
          <Grid item xs={12}>
            <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
              <Button className={classes.AddButton}>Add Items</Button>
            </div>
          </Grid>
        </Grid>
      </CustomModel>
    </div>
  );
}

export default SkinCare;
