import React, { useEffect, useRef, useState } from 'react';
import { Avatar, Button, Card, CardActions, CardContent, CircularProgress, Dialog, DialogActions, DialogContent, Grid, IconButton, makeStyles, Paper, Tab, Table, TableBody, FormControl, FormHelperText, TableCell, TableContainer, TableHead, TableRow, TextField, Tooltip, Typography } from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import AddIcon from '@material-ui/icons/Add';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination, TabContext, TabList, TabPanel } from '@material-ui/lab';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { Formik } from 'formik';
import * as Yup from 'yup';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import Page from '../../components/Page/Page';
import useConfModel from '../../hook/useConfModel';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';
import ControlPointIcon from '@material-ui/icons/ControlPoint';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';



const useStyles = makeStyles((theme: any) => ({
    root: {
      width: theme.breakpoints.values.lg,
      maxWidth: '100%',
      margin: '0 auto',
      padding: theme.spacing(3),
    },
    themeButton: {
        color: theme.palette.white,
        backgroundColor: theme.palette.green.main,
        '&:hover': {
          backgroundColor: theme.palette.green.dark,
        },
    },
    deleteButton: {
      width: '100%',
      height: '50px',
    },
    imageView : {
      width : '100%',
      height : '100%',
    }
  }));

function NutritionIngredients() {
  const classes = useStyles()
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Nutrition Ingredients',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const closeAddEditDialog = () =>{
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  }

  return (
    <div className={classes.root}>
      <Page title='Nutrition Ingredients' />
      <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
        <Grid item>
          <Typography component='h1' variant='h3'>
            Nutrition Ingredients
          </Typography>
        </Grid>
        <Grid item>
          <Button
            variant='contained'
            onClick={() => openAddDialog()}
            className={classes.themeButton}
            startIcon={<AddIcon />}
          >
            Add
          </Button>
        </Grid>
      </Grid>
      <ViewandEditDialogue {...addEditDialog}  onClose={closeAddEditDialog}/>
    </div>
  );
}



interface NutritionValues {
  nutrientName : string,
  image : {file : null | any ,prevImage : string , isNew : null | boolean},
  quantity : string,
  protien : string,
  fat : string,
  carbs : string,
  calories : string,
  benifits : Array<any>,
  description : string
}

const ViewandEditDialogue = (props: any) =>{
    const { isEdit, isOpen, okBtnText = 'OK', onClose,data, title, onSuccess} = props;
    const classes = useStyles()
    const formikRef = useRef<any>(null)
    const imageRef = useRef<any>(null)
    const initialValues: NutritionValues = {
        nutrientName : '',
        image : {file : null,prevImage : '' , isNew : null},
        quantity : '',
        protien : '',
        fat : '',
        carbs : '',
        calories : '',
        benifits : [],
        description : '',
    };
    const [initialFormValues,setInitialFormValues] = React.useState({...initialValues});
    const [addBenifits,setAddBenifits] = React.useState<any>([])

    const handleAddBenifits = (event: any) =>{
      event.preventDefault()
      const dummyData = [...addBenifits,''];
      setAddBenifits(dummyData)
      formikRef.current.setFieldValue('benifits',dummyData)
    }

    const handleBenifitChange = (event:any,index:any) =>{
      addBenifits[index] = event.target.value;
      formikRef.current.setFieldValue('benifits',[...addBenifits])
    }

    const onSubmit = (value: any,helper: any) =>{
      try{
        helper.setSubmitting(true)
        const { image, ...rest} = value;
        const { isNew, file} = image;
        const postData = rest;
        console.log(postData)
      }
      catch{
        Snackbar.show('Image Upload Failed', 'error');
      }
    }


    const onImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      e.persist();
      const files = e.target.files;
      if (files && files.length != 0) {
        const reader = new FileReader();
        const file = files[0];
        reader.onloadend = () => {
          formikRef.current.setFieldValue('image', {
            file,
            prevImage: reader.result,
            isNew: true,
          });
        };
        reader.readAsDataURL(file);
      } else {
        formikRef.current.setFieldValue('image', {
          file: null,
          prevImage: '',
          isNew: null,
        });
      }
    };

    return (
    <div>
       <Dialog 
        open={isOpen}
        disableBackdropClick
        disableEscapeKeyDown
        fullWidth
        maxWidth='md'
        aria-labelledby='dialog-title' >

        <DialogTitle id='dialog-title' onClose={onClose}>
          {title}
        </DialogTitle>
        <Formik
        innerRef={formikRef}
        enableReinitialize
        initialValues={initialFormValues}
        onSubmit={onSubmit}
        validationSchema={Yup.object().shape({
            nutrientName: Yup.string().trim().required('Name is required'),
            quantity : Yup.string().trim().required('Quality is required'),
            protien : Yup.string().trim().required('Protein is required'),
            fat : Yup.string().trim().required('Fat is required'),
            carbs : Yup.string().trim().required('Carbs is required'),
            calories : Yup.string().trim().required('Calories is required'),
            description : Yup.string().trim().required('Description is required'),
            image: Yup.object({
              file: Yup.mixed().required('A file is required'),
            }),
            benifits : Yup.array()
        })}
        >
            {({values, errors, touched, handleBlur,handleChange, submitForm, isSubmitting,})=>(
                <>
                <DialogContent dividers>
                    <Grid container spacing = {2}>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                label='Name'
                                name='nutrientName'
                                variant='outlined'
                                error={Boolean(touched.nutrientName && errors.nutrientName)}
                                helperText={touched.nutrientName && errors.nutrientName}
                                value={values.nutrientName}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                label='Quantity'
                                name='quantity'
                                variant='outlined'
                                error={Boolean(touched.quantity && errors.quantity)}
                                helperText={touched.quantity && errors.quantity}
                                value={values.quantity}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Protien'
                                name='protien'
                                variant='outlined'
                                error={Boolean(touched.protien && errors.protien)}
                                helperText={touched.protien && errors.protien}
                                value={values.protien}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Fat'
                                name='fat'
                                variant='outlined'
                                error={Boolean(touched.fat && errors.fat)}
                                helperText={touched.fat && errors.fat}
                                value={values.fat}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Carbs'
                                name='carbs'
                                variant='outlined'
                                error={Boolean(touched.carbs && errors.carbs)}
                                helperText={touched.carbs && errors.carbs}
                                value={values.carbs}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={6}>
                            <TextField
                                fullWidth
                                label='Calories'
                                name='calories'
                                variant='outlined'
                                error={Boolean(touched.calories && errors.calories)}
                                helperText={touched.calories && errors.calories}
                                value={values.calories}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item xs={12}>
                             <Button
                             fullWidth
                              className={classes.themeButton}
                              variant='contained'
                              color='default'
                              onClick={(e)=>handleAddBenifits(e)}
                              endIcon={<ControlPointIcon />}
                            >
                              Add Benifits
                            </Button>
                        </Grid>

                        {values?.benifits?.map((data: any,index: any)=>{
                          return (
                            <>
                              <Grid item xs={11}>
                                <TextField
                                    fullWidth
                                    label='Benifits'
                                    name={`benifits[${index}]`}
                                    variant='outlined'
                                    // error={Boolean(touched.calories && errors.calories)}
                                    // helperText={touched.calories && errors.calories}
                                    value={data}
                                    onChange={(e)=>handleBenifitChange(e,index)}
                                    onBlur={handleBlur}
                                />
                              </Grid>
                              <Grid item xs={1}>
                                <Button
                                  fullWidth
                                  className={classes.deleteButton}
                                  variant='contained'
                                  color='secondary'
                                  // onClick={() =>
                                  //   removeIngredients(values, i, setFieldValue)
                                  // }
                                >
                                  <DeleteIcon />
                                </Button>
                              </Grid>
                            </>
                          )
                        })}

                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                multiline
                                label='Description'
                                name='description'
                                variant='outlined'
                                error={Boolean(touched.description && errors.description)}
                                helperText={touched.description && errors.description}
                                value={values.description}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>

                        <Grid item md={12} xs={12}>
                          <input
                            name='image'
                            ref={imageRef}
                            type='file'
                            accept='.jpg,.png,jpeg'
                            onChange={onImageChange}
                            onBlur={handleBlur}
                            hidden
                          />
                          <Button
                            fullWidth
                            className={classes.themeButton}
                            variant='contained'
                            color='default'
                            onClick={() => imageRef.current.click()}
                            startIcon={<CloudUploadIcon />}
                          >
                            Upload Image
                          </Button>
                          <FormControl
                            error={Boolean(
                              touched?.image?.file && errors?.image?.file
                            )}
                          >
                            <FormHelperText>
                              {touched?.image?.file &&
                                errors?.image?.file}
                            </FormHelperText>
                          </FormControl>
                        </Grid>

                        <Grid item md={12} xs={12}>
                          {values.image?.prevImage && (
                            <img
                              className={classes.imageView}
                              src={values.image?.prevImage}
                            />
                          )}
                        </Grid>
                    </Grid>
                </DialogContent>
                <DialogActions>
                    <Button onClick={onClose} variant='outlined' color='secondary'>
                      Cancel
                    </Button>

                    <Button
                      className={classes.themeButton}
                      onClick={() => submitForm()}
                      disabled={isSubmitting}
                      variant='outlined'
                    >
                      {isSubmitting ? (
                        <CircularProgress size={24} style={{color : 'white'}} />
                      ) : (
                        okBtnText
                      )}
                    </Button>
                  </DialogActions>
                </>
            )}
        </Formik>

       </Dialog>
    </div>
    )
}

export default NutritionIngredients;
