import React, { useEffect, useRef, useState } from 'react';
import {
  makeStyles,
  Avatar,
  Card,
  CardActions,
  CardContent,
  InputLabel,
  Dialog,
  DialogActions,
  DialogContent,
  FormControl,
  FormHelperText,
  Grid,
  IconButton,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Select,
  ButtonGroup,
} from '@material-ui/core';
import { Formik } from 'formik';
import * as Yup from 'yup';
import AddIcon from '@material-ui/icons/Add';
import { Pagination } from '@material-ui/lab';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import Page from '../../components/Page/Page';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';
import useConfModel from '../../hook/useConfModel';
import { imageUpload } from '../../utils/FirebaseUtils';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import EditorButton from './EditorButtons';
import ControlPointIcon from '@material-ui/icons/ControlPoint';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  addIcon: {
    marginRight: theme.spacing(1),
  },
  content: {
    padding: 0,
  },
  inner: {
    minWidth: 700,
  },
  actions: {
    padding: theme.spacing(1),
    justifyContent: 'center',
  },
  tabCard: {
    marginTop: theme.spacing(3),
  },
  sEvenly: {
    display: 'flex',
    justifyContent: 'space-evenly',
  },
  iconPadd: {
    padding: 5,
  },
  jCenter: {
    display: 'flex',
    justifyContent: 'center',
  },
  imageView: {
    width: '100%',
    height: '100%',
  },
  themeButton: {
    color: theme.palette.white,
    backgroundColor: theme.palette.green.main,
    '&:hover': {
      backgroundColor: theme.palette.green.dark,
    },
  },
  lColor: {
    color: 'white',
  },
  addIngredients: {
    width: '100%',
    display: 'flex',
    justifyContent: 'space-between',
    height: theme.spacing(6.5),
    backgroundColor: 'white',
    padding: theme.spacing(2),
    border: '1px solid #e3e3e3',
    cursor: 'pointer',
  },
  avatar : {
    width : theme.spacing(15),
    height : theme.spacing(15)
  }
}));

const SkinCareRecipe = () => {
  const classes = useStyles();
  const { Post } = useService();
  const Snackbar = useSnackbar();
  const ConfModel = useConfModel();
  const [stateData, setStateData] = useState({ page_no: 1, page_limit: 10 });
  const [pageCount, setPageCount] = useState(0);
  const [dataList, setDataList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [addEditDialog, setAddEditDialog] = useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });
  const [viewDialog, setViewDialog] = useState({
    isOpen: false,
    title: '',
    data: {},
  });

  const listSkinCare = async () => {
    setLoading(true);
    Post('app/listSkinCare', stateData)
      .then((res: any) => {
        console.log('listSkinCare', res);
        setLoading(false);
        if (!res.error) {
          setDataList(res.data);
          setPageCount(res.page_count);
        } else {
          Snackbar.show(res.message, 'error');
        }
      })
      .catch((err: any) => {
        console.log('err', err);
        setLoading(false);
        Snackbar.show(err.message, 'error');
      });
  };

  const onPageChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setStateData((prevState: any) => ({ ...prevState, page_no: value }));
  };

  const setElipsis = (text: any) => {
    return text.length >= 25 ? `${text.substring(0, 40)}...` : text;
  };

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      isEdit: false,
      title: 'Add Recpie',
      okBtnText: 'Save',
    }));
  };

  const openEditDialog = (data: any) => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      isEdit: true,
      data,
      title: 'Edit Recpie',
      okBtnText: 'Edit',
    }));
  };

  const openViewDialog = (data: any) => {
    setViewDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      data,
      title: 'View Recpie',
    }));
  };

  const onDelete = (data: any) => {
    const { openModel, setLoading, closeModel } = ConfModel;
    const submitFunction = () => {
      setLoading(true);
      Post('app/deleteIngredient', { id: data._id })
        .then(async (res: any) => {
          setLoading(false);
          closeModel();
          onSuccessAction();
          Snackbar.show(res.message, 'success');
        })
        .catch((err: any) => {
          setLoading(false);
          Snackbar.show('Internal Server Error', 'error');
        });
    };
    openModel(submitFunction);
  };

  const closeAddEditDialog = () => {
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  const closeViewDialog = () => {
    setViewDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  const onSuccessAction = () => {
    listSkinCare();
    closeAddEditDialog();
  };

  useEffect(() => {
    listSkinCare();
  }, [stateData]);

  return (
    <div className={classes.root}>
      <Page title='Skin Care Recipe' />

      {/* =======Header====== */}
      <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
        <Grid item>
          <Typography component='h1' variant='h3'>
            Skin Care Recipe
          </Typography>
        </Grid>
        <Grid item>
          <Button
            variant='contained'
            onClick={() => openAddDialog()}
            className={classes.themeButton}
            startIcon={<AddIcon />}
          >
            Add
          </Button>
        </Grid>
      </Grid>

      {/* =============Search======== */}
      <Grid container spacing={3}>
        <Grid item>
          <Paper elevation={0}>
            <TextField
              fullWidth
              size='small'
              placeholder='Name'
              variant='outlined'
              name='state'
            />
          </Paper>
        </Grid>
      </Grid>

      {/* ========Table With Pagination========= */}
      <Card className={classes.tabCard}>
        <CardContent className={classes.content}>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell align='center'>#</TableCell>
                  <TableCell align='center'>Image</TableCell>
                  <TableCell align='center'>Name</TableCell>
                  <TableCell align='center'>Description</TableCell>
                  <TableCell align='center'>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {!loading &&
                  dataList.map((data: any, index: number) => (
                    <TableRow hover key={index}>
                      <TableCell align='center'>
                        {stateData.page_limit * (stateData.page_no - 1) +
                          index +
                          1}
                      </TableCell>
                      <TableCell align='center'>
                        <div className={classes.jCenter}>
                          <Avatar variant='square' src={data?.recipe_image} />
                        </div>
                      </TableCell>
                      <TableCell align='center'>{data?.recipe_name}</TableCell>
                      <TableCell align='center'>
                        <Tooltip title={data?.recipe_description}>
                          <span>{setElipsis(data?.recipe_description)}</span>
                        </Tooltip>
                      </TableCell>
                      <TableCell align='center'>
                        <div className={classes.sEvenly}>
                          <Tooltip title='View' arrow>
                            <IconButton
                              className={classes.iconPadd}
                              onClick={() => openViewDialog(data)}
                            >
                              <CenterFocusStrongIcon color='primary' />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title='Edit' arrow>
                            <IconButton
                              className={classes.iconPadd}
                              onClick={() => openEditDialog(data)}
                            >
                              <EditIcon color='action' />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title='Delete' arrow>
                            <IconButton
                              className={classes.iconPadd}
                              onClick={() => onDelete(data)}
                            >
                              <DeleteIcon color='secondary' />
                            </IconButton>
                          </Tooltip>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                {loading && <TableLoader />}
                {!loading && dataList.length == 0 && (
                  <TableNoData>No Data Found</TableNoData>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>

        <CardActions className={classes.actions}>
          <Pagination
            count={pageCount}
            page={stateData.page_no}
            onChange={onPageChange}
          />
        </CardActions>
      </Card>

      {/* ============Add Edit Dialog========== */}
      <AddEditDailog
        {...addEditDialog}
        onClose={closeAddEditDialog}
        onSuccess={onSuccessAction}
      />

      {/* ============View Dialog========== */}
      <ViewDailog {...viewDialog} onClose={closeViewDialog} />
    </div>
  );
};

const AddEditDailog = (props: any) => {
  const {
    isOpen,
    title,
    onClose,
    isEdit,
    data,
    onSuccess,
    okBtnText = 'Ok',
  } = props;
  const classes = useStyles();
  const formikRef = useRef<any>(null);
  const imageRef = useRef<any>(null);
  const Snackbar = useSnackbar();
  const { Post } = useService();

  const editor = useEditor({
    extensions: [StarterKit],
    content: '<p>Hello World! 🌎️</p>',
  });

  console.log(editor);
  const [dropDownValue, setDropDownValue] = useState([
    {
      id: null,
      quantity: null,
    },
  ]);
  const initialFormValue = {
    recipeName: '',
    recipeDescription: '',
    skinType: '',
    skinDescription: '',
    ingredients : [{
      id: '',
      quantity: '',
    }],
    image: { file: null, prevImage: '', isNew: null },
  };

  const [initialValue, setInitialValue] = useState({ ...initialFormValue });

  const onImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.persist();
    const files = e.target.files;
    if (files && files.length != 0) {
      const reader = new FileReader();
      const file = files[0];
      reader.onloadend = () => {
        formikRef.current.setFieldValue('image', {
          file,
          prevImage: reader.result,
          isNew: true,
        });
      };
      reader.readAsDataURL(file);
    } else {
      formikRef.current.setFieldValue('image', {
        file: null,
        prevImage: '',
        isNew: null,
      });
    }
  };

  const onSubmit = (value: any, helper: any) => {
    try {
      helper.setSubmitting(true);
      const render = async () => {
        const { image, ...rest } = value;
        const { file, isNew } = image;
        const PostData = rest;
        if (isNew) {
          PostData.image = await imageUpload(file);
        } else {
          PostData.image = file;
        }
        !isEdit && addData(PostData, helper);
        isEdit && editData(PostData, helper);
      };
      render();
    } catch (err) {
      Snackbar.show('Image Upload Failed', 'error');
    }
  };

  const addData = (data: any, { setSubmitting, resetForm }: any) => {
    setSubmitting(true);
    Post('app/addIngredient', data)
      .then((res: any) => {
        Snackbar.show(res.message, 'success');
        setSubmitting(false);
        resetForm();
        onSuccess();
      })
      .catch((err: any) => {
        Snackbar.show(err.message, 'error');
      });
  };

  const editData = (data: any, { setSubmitting, resetForm }: any) => {
    setSubmitting(true);
    Post('app/editIngredient', data)
      .then((res: any) => {
        Snackbar.show(res.message, 'success');
        setSubmitting(false);
        resetForm();
        onSuccess();
      })
      .catch((err: any) => {
        Snackbar.show(err.message, 'error');
      });
  };

  const handleDropdownOnChange = (event: React.ChangeEvent<{ value: any }>,index: any) => {
    const localData = [...dropDownValue];
    localData[index].id = event.target.value;
    setDropDownValue(localData)
    if(localData[index].id !== null){
      formikRef.current.setFieldValue('ingredients',localData[index].id = event.target.value)
    }
  };

  const addMoreCategories = () => {
    const addAnotherCategories = [...dropDownValue];
    addAnotherCategories.push({id: null, quantity: null });
    setDropDownValue(addAnotherCategories)
  };

  useEffect(() => {
    if (isEdit) {
      const { image, _id, ...rest } = data;
      const EditData = { ...rest, id: _id };
      EditData.image = { file: image, prevImage: image, isNew: false };
      setInitialValue(EditData);
    } else {
      setInitialValue(initialFormValue);
    }
  }, [props]);

  return (
    <Dialog
      disableBackdropClick
      disableEscapeKeyDown
      fullWidth
      maxWidth='md'
      aria-labelledby='dialog-title'
      open={isOpen}
    >
      <DialogTitle id='dialog-title' onClose={onClose}>
        {title}
      </DialogTitle>

      <Formik
        innerRef={formikRef}
        enableReinitialize
        initialValues={initialValue}
        onSubmit={onSubmit}
        validationSchema={Yup.object().shape({
          recipeName: Yup.string().trim().required('Recipe name is required'),
          skinType: Yup.string().trim().required('Skin type is required'),
          ingredients: Yup.array().required('Ingredients is required'),
          image: Yup.object({
            file: Yup.mixed().required('A file is required'),
          }),
        })}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          submitForm,
          isSubmitting,
        }) => (
          <>
            <DialogContent dividers>
              <Grid container spacing={3}>
                <Grid item md={12} xs={12}>
                  <TextField
                    fullWidth
                    label='Recipe Name'
                    name='recipeName'
                    variant='outlined'
                    error={Boolean(touched.recipeName && errors.recipeName)}
                    helperText={touched.recipeName && errors.recipeName}
                    value={values.recipeName}
                    onChange={handleChange}
                    onBlur={handleBlur}
                  />
                </Grid>

                <Grid item md={12} xs={12}>
                  <TextField
                    fullWidth
                    multiline
                    label='Recipe Description'
                    name='recipeDescription'
                    variant='outlined'
                    error={Boolean(
                      touched.recipeDescription && errors.recipeDescription
                    )}
                    helperText={
                      touched.recipeDescription && errors.recipeDescription
                    }
                    value={values.recipeDescription}
                    onChange={handleChange}
                    onBlur={handleBlur}
                  />
                </Grid>

                <Grid item md={12} xs={12}>
                  <TextField
                    fullWidth
                    label='Skin Type'
                    name='recipeName'
                    variant='outlined'
                    error={Boolean(touched.skinType && errors.skinType)}
                    helperText={touched.skinType && errors.skinType}
                    value={values.skinType}
                    onChange={handleChange}
                    onBlur={handleBlur}
                  />
                </Grid>

                <Grid item md={12} xs={12}>
                  <TextField
                    fullWidth
                    multiline
                    label='Skin Description'
                    name='recipeDescription'
                    variant='outlined'
                    error={Boolean(
                      touched.skinDescription && errors.skinDescription
                    )}
                    helperText={
                      touched.skinDescription && errors.skinDescription
                    }
                    value={values.skinDescription}
                    onChange={handleChange}
                    onBlur={handleBlur}
                  />
                </Grid>

                <Grid item md={12} xs={12}>
                  <Paper
                    className={classes.addIngredients}
                    elevation={0}
                    component='div'
                  >
                    <Typography>Ingredients</Typography>
                    <ControlPointIcon onClick={()=>values.ingredients.push({id: '',quantity: '',})}/>
                  </Paper>
                </Grid>

                {values.ingredients?.map((values: any,index: any) => {
                  return (
                    <>
                      <Grid key={index} item md={6} xs={12}>
                        <FormControl
                          className={classes.imageView}
                          variant='outlined'
                        >
                          <InputLabel htmlFor='outlined-age-native-simple'>
                            Ingredients
                          </InputLabel>
                          <Select
                            native
                            onChange={(event) => {
                              handleDropdownOnChange(event,index);
                              handleChange('ingredients');
                            }}
                            error={Boolean(
                              touched.ingredients && errors.ingredients
                            )}
                            label='Ingredients'
                            inputProps={{
                              name: 'ingredients',
                              id: 'outlined-age-native-simple',
                            }}
                          >
                            <option aria-label='None' value='' />
                            <option value={10}>Ten</option>
                            <option value={20}>Twenty</option>
                            <option value={30}>Thirty</option>
                          </Select>
                          <Typography color = 'error' variant = 'body2'>{ touched.ingredients && errors.ingredients}</Typography>
                        </FormControl>
                      </Grid>

                      <Grid item md={6} xs={12}>
                        <TextField
                          fullWidth
                          multiline
                          label='Skin Description'
                          name='recipeDescription'
                          variant='outlined'
                          error={Boolean(
                            touched.skinDescription && errors.skinDescription
                          )}
                          helperText={
                            touched.skinDescription && errors.skinDescription
                          }
                          value={values.skinDescription}
                          onChange={handleChange}
                          onBlur={handleBlur}
                        />
                      </Grid>
                    </>
                  );
                })}

                <Grid item md={12} xs={12}>
                  <EditorButton editor={editor} />
                  <EditorContent editor={editor} />
                </Grid>

                <Grid item md={12} xs={12}>
                  <input
                    name='image'
                    ref={imageRef}
                    type='file'
                    accept='.jpg,.png,jpeg'
                    onChange={onImageChange}
                    onBlur={handleBlur}
                    hidden
                  />
                  <Button
                    fullWidth
                    className={classes.themeButton}
                    variant='contained'
                    color='default'
                    onClick={() => imageRef.current.click()}
                    startIcon={<CloudUploadIcon />}
                  >
                    Recipe Image
                  </Button>
                  <FormControl
                    error={Boolean(touched?.image?.file && errors?.image?.file)}
                  >
                    <FormHelperText>
                      {touched?.image?.file && errors?.image?.file}
                    </FormHelperText>
                  </FormControl>
                </Grid>

                <Grid item md={12} xs={12}>
                  {values.image?.prevImage && (
                    <img
                      className={classes.imageView}
                      src={values.image?.prevImage}
                    />
                  )}
                </Grid>
              </Grid>
            </DialogContent>

            <DialogActions>
              <Button onClick={onClose} variant='outlined' color='secondary'>
                Cancel
              </Button>

              <Button
                className={classes.themeButton}
                onClick={() => submitForm()}
                disabled={isSubmitting}
                variant='outlined'
              >
                {isSubmitting ? (
                  <CircularProgress size={24} className={classes.lColor} />
                ) : (
                  okBtnText
                )}
              </Button>
            </DialogActions>
          </>
        )}
      </Formik>
    </Dialog>
  );
};

