  const { isOpen, title, onClose, data } = props;
  const classes = useStyles();
  const [formValue, setFormValue] = useState(data);

  useEffect(() => {
    setFormValue(data);
  }, [props]);

  console.log(formValue)

  return (
    <Dialog
      disableBackdropClick
      disableEscapeKeyDown
      fullWidth
      maxWidth='sm'
      aria-labelledby='dialog-view-title'
      open={isOpen}
    >
      <DialogTitle id='dialog-view-title' onClose={onClose}>
        {title}
      </DialogTitle>

      <DialogContent dividers>
        <Grid container spacing={3}>
          <Grid item md={5} xs={6}>
            <Avatar className = {classes.avatar} variant='square' src={'https://dontmesswithmama.com/wp-content/uploads/2013/06/diy-skincare-recipes-homemade-natural.jpg'} />
          </Grid>
          <Grid item md={7} xs={6}>
            {formValue?.recipe_description}
          </Grid>
          <Grid item md={12} xs={12}>
            {formValue?.recipe_description}
          </Grid>
          <Grid item md={12} xs={12}>
            {formValue?.image && (
              <img className={classes.imageView} src={formValue?.image} />
            )}
          </Grid>
        </Grid>
      </DialogContent>

      <DialogActions>
        <Button onClick={onClose} variant='outlined' color='secondary'>
          Close
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default SkinCareRecipe;
