export const AddEditModel = (props: any) =>{
    const classes = useStyle()
    const { isEdit, isOpen, okBtnText = 'OK', onClose, data, title, onSuccess } = props;
    const [initialFormValues, setInitialFormValues] = React.useState({ ...initialValues});
    const formikRef = React.useRef<any>(null);
    const imageRef = React.useRef<any>(null);

    const onSubmit = () =>{
        alert('done')
    }

    const handleOptionChange = (event: React.ChangeEvent<HTMLInputElement>, value: any) => {
        formikRef.current.setFieldValue('blog_type', value?.id);
    }

    const onImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        event.persist();
        const files = event.target.files;
        if (files && files.length != 0) {
          const reader = new FileReader();
          const file = files[0];
          reader.onloadend = () => {
            formikRef.current.setFieldValue('image', {
              file,
              prevImage: reader.result,
              isNew: true,
            });
          };
          reader.readAsDataURL(file);
        } else {
          formikRef.current.setFieldValue('image', {
            workoutTermsName: '',
            image: { file: null, prevImage: '', isNew: null },
            description: '',
          });
        }
    }

    return (
        <Dialog
            open={isOpen}
            disableBackdropClick
            disableEscapeKeyDown
            fullWidth
            maxWidth='md'
            aria-labelledby='dialog-title'
        >
            <DialogTitle id='dialog-title' onClose={onClose}>
            {title}
            </DialogTitle>
            <Formik
                innerRef={formikRef}
                enableReinitialize
                initialValues={initialValues}
                onSubmit={onSubmit}
                validationSchema={Yup.object().shape({
                    description : Yup.string().trim().required('Blog Description is required'),
                    blog_type : Yup.string().trim().required('Blog Type is required'),
                    title : Yup.string().trim().required('Blog Title is required'),
                    image: Yup.object({ file: Yup.mixed().required('A file is required')}),
                    image_thumbnail: Yup.object({ file: Yup.mixed().required('A file is required')}),
                })}
            >
                {({ values, errors, touched, handleBlur, handleChange, setFieldValue, submitForm, setFieldTouched, isSubmitting, }) => (
                    <>
                    <DialogContent dividers>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label='Title'
                                    name='title'
                                    variant='outlined'
                                    value={values.title}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    error={Boolean(touched.title && errors.title)}
                                    helperText={touched.title && errors.title}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <Autocomplete
                                    fullWidth
                                    options={blogTypes}
                                    // value={ingredientsList.find(
                                    //     (data: any) => data._id == items.id
                                    // )}
                                    getOptionLabel={(option: any) => option.name}
                                    onChange={(event: any, newValue) => handleOptionChange(event, newValue)}
                                    onBlur={handleBlur}
                                    renderInput={(params) => (
                                        <TextField
                                            {...params}
                                            label='Ingredients'
                                            variant='outlined'
                                            error={Boolean(touched.blog_type && errors.blog_type)}
                                            helperText={touched.blog_type && errors.blog_type}
                                            inputProps={{
                                                ...params.inputProps,
                                            }}
                                        />
                                    )}
                                />
                            </Grid>
                            <Grid item md={12} xs={12}>
                                <FormControl
                                    fullWidth
                                    error={Boolean(
                                        touched?.description &&
                                        errors?.description
                                    )}
                                >
                                    <TipTapEditor
                                        value={values.description}
                                        onChange={(value: any) => setFieldValue('description', value)}
                                        onBlur={() => setFieldTouched('description', true, true)}
                                    />

                                    <FormHelperText>
                                        {touched?.description &&
                                        errors?.description}
                                    </FormHelperText>
                                </FormControl>
                            </Grid>
                            <Grid item md={6} xs={12}>
                                <input
                                    name='image'
                                    ref={imageRef}
                                    type='file'
                                    accept='.jpg,.png,jpeg'
                                    onChange={onImageChange}
                                    onBlur={handleBlur}
                                    hidden
                                />
                                    <Button
                                        fullWidth
                                        className={classes.themeButton}
                                        variant='contained'
                                        color='default'
                                        onClick={() => imageRef.current.click()}
                                        startIcon={<CloudUploadIcon />}
                                    >
                                    Upload Image
                                    </Button>
                                    <FormControl
                                        error={Boolean(
                                            touched?.image?.file && errors?.image?.file
                                        )}
                                    >
                                    <FormHelperText>
                                        {touched?.image?.file &&
                                        errors?.image?.file}
                                    </FormHelperText>
                                </FormControl>
                            </Grid>

                            <Grid item md={6} xs={12}>
                                <input
                                    name='image'
                                    ref={imageRef}
                                    type='file'
                                    accept='.jpg,.png,jpeg'
                                    // onChange={onImageChange}
                                    onBlur={handleBlur}
                                    hidden
                                />
                                    <Button
                                        fullWidth
                                        className={classes.themeButton}
                                        variant='contained'
                                        color='default'
                                        onClick={() => imageRef.current.click()}
                                        startIcon={<CloudUploadIcon />}
                                    >
                                    Upload Thumbnail
                                    </Button>
                                    <FormControl
                                        error={Boolean(
                                            touched?.image?.file && errors?.image?.file
                                        )}
                                    >
                                    <FormHelperText>
                                        {touched?.image?.file &&
                                        errors?.image?.file}
                                    </FormHelperText>
                                </FormControl>
                            </Grid>
                        </Grid>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={onClose} variant='outlined' color='secondary'>
                            Cancel
                        </Button>

                        <Button
                            className={classes.themeButton}
                            onClick={() => submitForm()}
                            disabled={isSubmitting}
                            variant='outlined'
                        >
                            {isSubmitting ? (
                                <CircularProgress size={24} style={{ color: 'white' }} />
                            ) : (
                                okBtnText
                            )}
                        </Button>
                    </DialogActions>
                    </>
                )}
            </Formik>
        </Dialog>
    )
}

