import React, { useEffect, useRef, useState } from 'react';
import {
  makeStyles,
  Avatar,
  Card,
  CardActions,
  CardContent,
  InputLabel,
  Dialog,
  DialogActions,
  DialogContent,
  FormControl,
  FormHelperText,
  Grid,
  IconButton,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Select,
} from '@material-ui/core';
import Autocomplete from '@material-ui/lab/Autocomplete';
import AddIcon from '@material-ui/icons/Add';
import Page from '../../components/Page/Page';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { Formik } from 'formik';
import * as Yup from 'yup';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination } from '@material-ui/lab';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import useSnackbar from '../../hook/useSnackbar';
import useService from '../../hook/useService';
import useConfModel from '../../hook/useConfModel';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  themeButton: {
    color: theme.palette.white,
    backgroundColor: theme.palette.green.main,
    '&:hover': {
      backgroundColor: theme.palette.green.dark,
    },
  },
  widthAndHeight: {
    width: '100%',
    height: '100%',
  },
  addIngredients: {
    width: '100%',
    height: theme.spacing(6.5),
    backgroundColor: theme.palette.green.main,
    color: 'white',
    padding: theme.spacing(2),
  },
  lColor: {
    color: 'white',
  },
  tabCard: {
    marginTop: theme.spacing(3),
  },
  content: {
    padding: 0,
  },
  jCenter: {
    display: 'flex',
    justifyContent: 'center',
  },
  sEvenly: {
    display: 'flex',
    justifyContent: 'space-evenly',
  },
  iconPadd: {
    padding: 5,
  },
  actions: {
    padding: theme.spacing(1),
    justifyContent: 'center',
  },
}));

const skinTypes = [
  { title: 'Dry', id: 1 },
  { title: 'Oily', id: 2 },
  { title: 'Mixed', id: 3 },
];

const skinIssues = [
  { title: 'Acne', id: 1 },
  { title: 'Dry Skin', id: 2 },
];

function SkinCarePlan() {
  const classes = useStyles();
  const { Post } = useService();
  const Snackbar = useSnackbar();
  const ConfModel = useConfModel();
  const [isLoading, setIsLoading] = React.useState(true);
  const [stateData, setStateData] = React.useState({
    page_no: 1,
    page_limit: 10,
  });
  const [pageCount, setPageCount] = React.useState(0);
  const [tableData, setTableData] = React.useState([]);
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });

  const getListofSkinCareplan = () => {
    Post('app/listSkinCarePlan', stateData)
      .then((res: any) => {
        setIsLoading(false);
        console.log(res.data);
        if (!res.error) {
          setPageCount(res.page_count);
          setTableData(res.data);
        } else {
          Snackbar.show(res.message, 'error');
        }
      })
      .catch((error: any) => {
        setIsLoading(false);
        Snackbar.show(error.message, 'error');
      });
  };

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Skin Care Plan',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const openEditDialog = (data: any) => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      isEdit: true,
      data,
      title: 'Edit Skin care Plan',
      okBtnText: 'Edit',
    }));
  };

  const onDelete = (data: any) => {
    const { openModel, setLoading, closeModel } = ConfModel;
    const submitFunction = () => {
      setLoading(true);
      Post('app/deleteSkinCarePlan', { id: data._id })
        .then(async (res: any) => {
          setLoading(false);
          closeModel();
          onSuccessAction();
          Snackbar.show(res.message, 'success');
        })
        .catch((err: any) => {
          setLoading(false);
          Snackbar.show('Internal Server Error', 'error');
        });
    };
    openModel(submitFunction);
  };

  const onSuccessAction = () => {
    getListofSkinCareplan();
    closeAddEditDialog();
  };

  const closeAddEditDialog = () => {
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  const onPageChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setStateData((prevState: any) => ({ ...prevState, page_no: value }));
  };

  useEffect(() => {
    getListofSkinCareplan();
  }, []);

  return (
    <div className={classes.root}>
      <Page title='Skin Care Plan' />

      {/* =======Header====== */}
      <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
        <Grid item>
          <Typography component='h1' variant='h3'>
            Skin Care Plan
          </Typography>
        </Grid>
        <Grid item>
          <Button
            variant='contained'
            onClick={() => openAddDialog()}
            className={classes.themeButton}
            startIcon={<AddIcon />}
          >
            Add
          </Button>
        </Grid>
      </Grid>

      {/* =============Search======== */}
      <Grid container spacing={3}>
        <Grid item>
          <Paper elevation={0}>
            <TextField
              fullWidth
              size='small'
              placeholder='Name'
              variant='outlined'
              name='state'
            />
          </Paper>
        </Grid>
      </Grid>

      {/* ========Table With Pagination========= */}
      <Card className={classes.tabCard}>
        <CardContent className={classes.content}>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell align='center'>#</TableCell>
                  <TableCell align='center'>Skin Type</TableCell>
                  <TableCell align='center'>Skin Issues</TableCell>
                  <TableCell align='center'>Recipes</TableCell>
                  <TableCell align='center'>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {!isLoading &&
                  tableData.map((data: any, index: any) => {
                    return (
                      <TableRow hover>
                        <TableCell align='center'>1</TableCell>
                        <TableCell align='center'>{data?.skin_type}</TableCell>
                        <TableCell align='center'>
                          {' '}
                          {data?.skin_issues}{' '}
                        </TableCell>
                        <TableCell align='center'>
                          {data?.recipes[0].recipe.recipe_name}
                        </TableCell>
                        <TableCell align='center'>
                          <div className={classes.sEvenly}>
                            <Tooltip title='View' arrow>
                              <IconButton
                                className={classes.iconPadd}
                                //   onClick={() => openViewDialog(data)}
                              >
                                <CenterFocusStrongIcon color='primary' />
                              </IconButton>
                            </Tooltip>
                            <Tooltip title='Edit' arrow>
                              <IconButton
                                className={classes.iconPadd}
                                onClick={() => openEditDialog(data)}
                              >
                                <EditIcon color='action' />
                              </IconButton>
                            </Tooltip>
                            <Tooltip title='Delete' arrow>
                              <IconButton
                                className={classes.iconPadd}
                                onClick={() => onDelete(data)}
                              >
                                <DeleteIcon color='secondary' />
                              </IconButton>
                            </Tooltip>
                          </div>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                {isLoading && <TableLoader />}
                {!isLoading && tableData.length == 0 && (
                  <TableNoData>No Data Found</TableNoData>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>

        <CardActions className={classes.actions}>
          <Pagination
            count={pageCount}
            page={stateData.page_no}
            onChange={onPageChange}
          />
        </CardActions>
      </Card>

      {/* ==========Add & Edit Dialoge======== */}

      <AddEditDialog
        {...addEditDialog}
        onClose={closeAddEditDialog}
        onSuccess={onSuccessAction}
      />
    </div>
  );
}

const AddEditDialog = (props: any) => {
  const { isEdit, isOpen, okBtnText = 'OK', onClose, title, onSuccess } = props;
  const classes = useStyles();
  const { Post } = useService();
  const Snackbar = useSnackbar();
  const formikRef = useRef<any>(null);
  const [skinCareRecipeList, setSkinCareRecipeList] = React.useState<any>([]);
  const [storedRecipe, seStoredRecipe] = React.useState<any>([]);
  const days = [1, 2, 3, 4, 5, 6, 7];

  const initialFormValues = {
    skinType: '',
    skinIssues: '',
    // recipe: '',
  };

  const listSKinCareList = () => {
    Post('app/listAllSkinCareRecipe', {})
      .then((response: any) => {
        if (!response.error) {
          let ingredients = response.data.map((data: any) => {
            data.id = data._id;
            return data;
          });
          setSkinCareRecipeList(ingredients);
        } else {
          Snackbar.show(response.message, 'error');
        }
      })
      .catch((error: any) => {
        Snackbar.show(error.message, 'error');
      });
  };

  const onSubmit = (value: any, helper: any) => {
    helper.setSubmitting(false);
    let postData = {
      skin_type: value.skinType,
      skin_issues: value.skinIssues,
      recipes: storedRecipe,
    };
    addData(postData, helper);
  };

  const addData = (data: any, { setSubmitting, resetForm }: any) => {
    setSubmitting(true);
    Post('app/addSkinCareRecipe', data)
      .then((res: any) => {
        Snackbar.show(res.message, 'success');
        setSubmitting(false);
        resetForm();
        onSuccess();
        seStoredRecipe([]);
      })
      .catch((err: any) => {
        setSubmitting(false);
        Snackbar.show(err.message, 'error');
      });
  };

  const handleOnchange = (event: any, newValue: any, date: any) => {
    let sampleData = [...storedRecipe];
    sampleData.push({ id: newValue.id, day: date });
    seStoredRecipe(sampleData);
  };

  const handleSkinType = (event: any, newValue: any) => {
    formikRef.current.setFieldValue('skinType', newValue.title);
  };

  const handleSkinIssues = (event: any, newValue: any) => {
    formikRef.current.setFieldValue('skinIssues', newValue.title);
  };

  useEffect(() => {
    listSKinCareList();
  }, []);

  console.log(storedRecipe);

  return (
    <div>
      <Dialog
        open={isOpen}
        disableBackdropClick
        disableEscapeKeyDown
        fullWidth
        maxWidth='md'
        aria-labelledby='dialog-title'
      >
        <DialogTitle id='dialog-title' onClose={onClose}>
          {title}
        </DialogTitle>
        <Formik
          innerRef={formikRef}
          enableReinitialize
          initialValues={initialFormValues}
          onSubmit={onSubmit}
          validationSchema={Yup.object().shape({
            skinType: Yup.string().trim().required('Recipe name is required'),
            skinIssues: Yup.string()
              .trim()
              .required('Recipe Issues is required'),
            // recipe: Yup.string().trim().required('Recipe name is required'),
          })}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            submitForm,
            isSubmitting,
          }) => (
            <>
              {console.log(errors)}
              <DialogContent dividers>
                <Grid container spacing={3}>
                  <Grid item xs={6}>
                    <Autocomplete
                      options={skinTypes}
                      getOptionLabel={(option: any) => option.title}
                      onChange={handleSkinType}
                      renderInput={(params: any) => (
                        <TextField
                          {...params}
                          label='Skin Types'
                          variant='outlined'
                          error={Boolean(touched.skinType && errors.skinType)}
                          helperText={touched.skinType && errors.skinType}
                          inputProps={{
                            ...params.inputProps,
                          }}
                        />
                      )}
                    />
                  </Grid>
                  <Grid item xs={6}>
                    <Autocomplete
                      options={skinIssues}
                      getOptionLabel={(option: any) => option.title}
                      onChange={handleSkinIssues}
                      renderInput={(params: any) => (
                        <TextField
                          {...params}
                          label='Skin Issues'
                          variant='outlined'
                          error={Boolean(
                            touched.skinIssues && errors.skinIssues
                          )}
                          helperText={touched.skinIssues && errors.skinIssues}
                          inputProps={{
                            ...params.inputProps,
                          }}
                        />
                      )}
                    />
                  </Grid>
                  {days.map((data: any, index: any) => {
                    return (
                      <>
                        <Grid key={index} item xs={1}>
                          <Paper
                            elevation={0}
                            component='div'
                            className={classes.addIngredients}
                          >
                            <Typography
                              className={classes.lColor}
                              align={'center'}
                              variant={'h6'}
                            >
                              {data}
                            </Typography>
                          </Paper>
                        </Grid>
                        <Grid item xs={11}>
                          <Autocomplete
                            id='combo-box-demo'
                            options={skinCareRecipeList}
                            // value={skinCareRecipeList.find(
                            //   (data: any) => data._id
                            // )}
                            getOptionLabel={(option: any) => option.recipe_name}
                            getOptionSelected={(option) => option._id}
                            onChange={(event: any, newValue: any) =>
                              handleOnchange(event, newValue, data)
                            }
                            onBlur={handleBlur}
                            renderInput={(params: any) => (
                              <TextField
                                {...params}
                                label='Recipes'
                                name='Recipes1'
                                variant='outlined'
                                inputProps={{
                                  ...params.inputProps,
                                }}
                              />
                            )}
                          />
                        </Grid>
                      </>
                    );
                  })}
                </Grid>
              </DialogContent>

              <DialogActions>
                <Button onClick={onClose} variant='outlined' color='secondary'>
                  Cancel
                </Button>

                <Button
                  className={classes.themeButton}
                  onClick={() => submitForm()}
                  disabled={isSubmitting}
                  variant='outlined'
                >
                  {isSubmitting ? (
                    <CircularProgress size={24} className={classes.lColor} />
                  ) : (
                    okBtnText
                  )}
                </Button>
              </DialogActions>
            </>
          )}
        </Formik>
      </Dialog>
    </div>
  );
};

export default SkinCarePlan;
