import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import SearchBar from '../../components/Common/SearchBar/Searchbar';
import CustomModel from '../../components/Common/DialogBox/DialogBox';
import Typography from '@material-ui/core/Typography';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import TextField from '@material-ui/core/TextField';
import AddIcon from '@material-ui/icons/Add';

const useStyles = makeStyles((theme) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  textFieldStyle: {
    width: '100%',
    marginBottom: theme.spacing(1),
  },
  textAreaStyle: {
    borderRadius: 5,
    width: '100%',
    height: '100px !important',
    paddingLeft: 4,
    paddingTop: 10,
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    outline: 'none',
    border: '1px solid #c5c3c3',
    marginBottom: theme.spacing(1),
  },
  skinItems: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  skinItemstextfield: {
    width: '48%',
    marginBottom: theme.spacing(1),
  },
  quantityStyle: {
    width: '70%',
    marginBottom: theme.spacing(1),
  },
}));

function SkinCare() {
  const classes = useStyles();
  const [addModelOpen, setAddModelOpen] = React.useState(false);
  return (
    <div className={classes.root}>
      <SearchBar modelOpen={() => setAddModelOpen(!addModelOpen)} />
      <CustomModel
        open={addModelOpen}
        close={() => setAddModelOpen(!addModelOpen)}
        Title=' Add Skin Care Items'
      >
        <TextField
          className={classes.textFieldStyle}
          placeholder='Enter Recipe Name'
          variant='outlined'
        />
        <TextareaAutosize
          className={classes.textAreaStyle}
          aria-label='minimum height'
          rowsMin={3}
          placeholder='Minimum 3 rows'
        />
        <div className={classes.skinItems}>
          <TextField
            className={classes.skinItemstextfield}
            placeholder='Enter Skin Type'
            variant='outlined'
          />
          <TextField
            className={classes.skinItemstextfield}
            placeholder='Enter Skin Issue'
            variant='outlined'
          />
        </div>
        <div className={classes.skinItems}>
          <AddIcon />
          <TextField
            className={classes.quantityStyle}
            placeholder='Quantity'
            variant='outlined'
          />
        </div>
        <TextField
          className={classes.textFieldStyle}
          placeholder='Enter Recipe Name'
          variant='outlined'
        />
        <TextareaAutosize
          className={classes.textAreaStyle}
          aria-label='minimum height'
          rowsMin={3}
          placeholder='Minimum 3 rows'
        />
      </CustomModel>
    </div>
  );
}

export default SkinCare;
