import {
  Avatar,
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  CardHeader,
  CardMedia,
  CircularProgress,
  CircularProgressProps,
  Dialog,
  DialogActions,
  DialogContent, Divider, FormControl,
  FormHelperText, Grid,
  IconButton,
  ListItem,
  ListItemAvatar,
  makeStyles,
  Paper,
  Tab,
  Table,
  TableBody, TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
  Tooltip, Typography
} from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItemText from '@material-ui/core/ListItemText';
import AddIcon from '@material-ui/icons/Add';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import CheckIcon from '@material-ui/icons/Check';
import ControlPointIcon from '@material-ui/icons/ControlPoint';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination, TabContext, TabList, TabPanel } from '@material-ui/lab';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { Formik, getIn } from 'formik';
import React, { useEffect, useRef, useState } from 'react';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import Page from '../../components/Page/Page';
import useConfModel from '../../hook/useConfModel';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';
import UnitSelect from '../../components/UnitSelect/UnitSelect';
import UnitDropdown from '../../utils/MetricUnits';
import { initialFormValues, NutMeal, NutMealTime, validation } from './FormikValues';
import clsx from 'clsx'

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  content: {
    padding: 0,
  },
  tabCard: {
    marginTop: theme.spacing(3),
  },
  themeButton: {
    color: theme.palette.white,
    backgroundColor: theme.palette.green.main,
    '&:hover': {
      backgroundColor: theme.palette.green.dark,
    },
  },
  sEvenly: {
    display: 'flex',
    justifyContent: 'space-evenly',
  },
  sBetween: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  iconPadd: {
    padding: 5,
  },
  jCenter: {
    display: 'flex',
    justifyContent: 'center',
  },
  actions: {
    padding: theme.spacing(1),
    justifyContent: 'center',
  },
  tabRoot: {
    minWidth: 0,
    textTransform: 'none'
    // margin: '10px 0px',
  },
  tabIndicator: {
    backgroundColor: theme.palette.green.main,
  },
  tabTextColorInherit: {
    backgroundColor: theme.palette.green.main,
    color: 'white',
    border: '1px solid white',
