sourcesContent:
import React from 'react'
import { Avatar, Button, Card, CardActions, CardContent, CircularProgress, Dialog, DialogActions, DialogContent, Grid, IconButton, makeStyles, Paper, Tab,  Table, TableBody, FormControl, FormHelperText, TableCell, TableContainer, TableHead, TableRow, TextField, Tooltip,  Divider, Typography, Tabs} from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import AddIcon from '@material-ui/icons/Add';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination, TabContext, TabList, TabPanel } from '@material-ui/lab';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { imageUpload } from '../../utils/FirebaseUtils';
import { Formik } from 'formik';
import * as Yup from 'yup';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import Page from '../../components/Page/Page';
import useConfModel from '../../hook/useConfModel';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';
import ControlPointIcon from '@material-ui/icons/ControlPoint';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import TimerIcon from '@material-ui/icons/Timer';


const useStyles = makeStyles((theme: any)=>({
    root: {
        width: theme.breakpoints.values.lg,
        maxWidth: '100%',
        margin: '0 auto',
        padding: theme.spacing(3),
    },
    themeButton: {
        color: theme.palette.white,
        backgroundColor: theme.palette.green.main,
        '&:hover': {
          backgroundColor: theme.palette.green.dark,
        },
    },
    workouttermsavatar: {
        width: '100%',
        height: '100%',
    },
    deleteButton: {
        width: '100%',
        height: '50px',
    },
    timeText: {
      display: 'flex',
      alignItems: 'center'
    },
    content: {
      padding: 0,
    },
    inner: {
      minWidth: 700,
    },
    actions: {
      padding: theme.spacing(1),
      justifyContent: 'center',
    },
    tabCard: {
      marginTop: theme.spacing(3),
    },
    sEvenly: {
      display: 'flex',
      justifyContent: 'space-evenly',
    },
    iconPadd: {
      padding: 5,
    },
    jCenter: {
      display: 'flex',
      justifyContent: 'center',
    },
    imageView : {
      width : '100%',
      height : '100%',
    },
    avatarRoot: {
      borderRadius: 10,
      marginRight: 15,
      width: theme.spacing(10),
      height: theme.spacing(10),
    },
    textPrimary: {
      marginTop: 10,
      fontWeight: 'bold',
    },
    textSecondary: {
      marginTop: 10,
      color: '#f0c100',
    },
    ingrdientsGridMain: {
      marginTop: 10,
    },
    ingredientsAvatarRoot: {
      width: theme.spacing(7),
      height: theme.spacing(7),
      margin: 'auto',
    },
    htmlContentGrid: {
      paddingLeft: theme.spacing(3),
      paddingRight: theme.spacing(3),
      paddingTop: theme.spacing(1),
    },
    noIngredientsText: {
      marginBottom: theme.spacing(3),
      display: 'flex',
      justifyContent: 'center',
    },
    htmlContent: {
      '& ul': {
        paddingLeft: '1.2rem',
      },
      '& p': {
        textAlign: 'justify',
      },
    },
}))
function MealRecipe() {
  const classes = useStyles();
  const ConfModel = useConfModel();
  const [loading, setLoading] = React.useState(false);
  const [mealRecipeTableData, setMealRecipeTableData] = React.useState([]);
  const [stateData, setStateData] =React.useState({ page_no: 1, page_limit: 10 });
  const [pageCount, setPageCount] =React.useState(0);
  const { Post } = useService();
  const Snackbar = useSnackbar();
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });
  const [viewDialog, setViewDialog] = React.useState({
    isOpen: false,
    title: '',
    data: {},
  });

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Meal Recipe',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const openEditDialog = (data: any) => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      isEdit: true,
      data,
      title: 'Edit Meal Recipe',
      okBtnText: 'Edit',
    }));
  };

  const openViewDialog = (data: any) => {
    setViewDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      data,
      title: 'View Meal Recipe',
    }));
  };

  const listMealRecipies = async () => {
    setLoading(true);
    Post('app/listMealRecipe', stateData)
      .then((res: any) => {
        setLoading(false);
        if (!res.error) {
          setMealRecipeTableData(res.data);
          setPageCount(res.page_count);
        } else {
          Snackbar.show(res.message, 'error');
        }
      })
      .catch((err: any) => {
        setLoading(false);
        Snackbar.show(err.message, 'error');
      });
  };

  const onDelete = (data: any) => {
    const { openModel, setLoading, closeModel } = ConfModel;
    const submitFunction = () => {
      setLoading(true);
      Post('app/deleteMealRecipe', { id: data._id })
        .then(async (res: any) => {
          setLoading(false);
          closeModel();
          onSuccessAction();
          Snackbar.show(res.message, 'success');
        })
        .catch((err: any) => {
          setLoading(false);
          Snackbar.show('Internal Server Error', 'error');
        });
    };
    openModel(submitFunction);
  };

  const closeAddEditDialog = () => {
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  const closeViewDialog = () => {
    setViewDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  };

  const onSuccessAction = () => {
    listMealRecipies();
    closeAddEditDialog();
  };


  const setElipsis = (text: any) => {
    return text?.length >= 25 ? `${text.substring(0, 40)}...` : text;
  };

  React.useEffect(() => {
    listMealRecipies();
  }, [stateData]);
  return (
    <div className={classes.root}>
        <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
            <Grid item>
            <Typography component='h1' variant='h3'>
                Meal Recipe
            </Typography>
            </Grid>
            <Grid item>
            <Button
                variant='contained'
                onClick={() => openAddDialog()}
                className={classes.themeButton}
                startIcon={<AddIcon />}
            >
                Add
            </Button>
            </Grid>
        </Grid>

      {/* =============Search======== */}
        <Grid container spacing={3}>
            <Grid item>
                <Paper elevation={0}>
                    <TextField
                    fullWidth
                    size='small'
                    placeholder='Name'
                    variant='outlined'
                    name='state'
                    />
                </Paper>
            </Grid>
        </Grid>

        <Card className={classes.tabCard}>
        <CardContent className={classes.content}>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell align='center'>#</TableCell>
                  <TableCell align='center'>Meal Recipe Image</TableCell>
                  <TableCell align='center'>Meal Recipe Name</TableCell>
                  <TableCell align='center'>Meal Recipe Description</TableCell>
                  <TableCell align='center'>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {!loading &&
                  mealRecipeTableData.map((data: any, index: number) => (
                    <TableRow hover key={index}>
                      <TableCell align='center'>
                        {stateData.page_limit * (stateData.page_no - 1) +
                          index +
                          1}
                      </TableCell>
                      <TableCell align='center'>
                        <div className={classes.jCenter}>
                          <Avatar variant='square' src={data?.image} />
                        </div>
                      </TableCell>
                      <TableCell align='center'>{data?.name}</TableCell>
                      <TableCell align='center'>
                        <Tooltip title={data?.preparation_description}>
                          <span>{setElipsis(data?.preparation_description)}</span>
                        </Tooltip>
                      </TableCell>
                      <TableCell align='center'>
                        <div className={classes.sEvenly}>
                          <Tooltip title='View' arrow>
                            <IconButton
                              className={classes.iconPadd}
                              onClick={() => openViewDialog(data)}
                            >
                              <CenterFocusStrongIcon color='primary' />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title='Edit' arrow>
                            <IconButton
                              className={classes.iconPadd}
                              onClick={() => openEditDialog(data)}
                            >
                              <EditIcon color='action' />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title='Delete' arrow>
                            <IconButton
                              className={classes.iconPadd}
                              onClick={() => onDelete(data)}
                            >
                              <DeleteIcon color='secondary' />
                            </IconButton>
                          </Tooltip>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                {loading && <TableLoader />}
                {!loading && mealRecipeTableData.length == 0 && (
                  <TableNoData>No Data Found</TableNoData>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>

        <CardActions className={classes.actions}>
          <Pagination
            count={pageCount}
            page={stateData.page_no}
            // onChange={onPageChange}
          />
        </CardActions>
      </Card>

        <AddEditModel
            {...addEditDialog}
            onClose={closeAddEditDialog}
            onSuccess={onSuccessAction}
         />

         <ViewModel {...viewDialog} onClose={closeViewDialog} />

    </div>
    )
}

interface MealTerms {
    name : string,
    image : {file:string | null, prevImage : string, isNew: boolean | null},
    term : string
}

interface Ingredients {
    id : string,
    quantity : string
}

interface MealPlan {
    name : string,
    image : {file:string | null, prevImage : string, isNew: boolean | null},
    nutrition : string,
    description : string,
    protein : string,
    fat : string,
    carbs : string,
    terms : MealTerms[],
    ingredients : Ingredients[],
    preparation_time : string,
    preparation_description : string

}

const mealTerms: MealTerms = {
    name : '',
    image : { file:null , prevImage : '', isNew: null},
    term : ''
}

const ingredientsSelect: Ingredients = {
    id : '',
    quantity:''
}

const initialFormValues: MealPlan = {
    name : '',
    image : { file:null, prevImage : '', isNew : null },
    nutrition : '',
    description : '',
    protein : '',
    fat : '',
    carbs : '',
    terms : [mealTerms],
    ingredients : [ingredientsSelect],
    preparation_time : '',
    preparation_description : ''
}

export const AddEditModel = (props: any) =>{
    const { isEdit, isOpen, okBtnText = 'OK', onClose, data, title, onSuccess } = props;
    const classes = useStyles()
    const [initialValues, setInitialValues] = React.useState({...initialFormValues});
    const formikRef = React.useRef<any>(null);
    const imageRef = React.useRef<any>(null);
    const avatharimgRef = React.useRef<any>(null);
    const [allIngredients, setAllIngredients] = React.useState([])
    const { Post } = useService();
    const Snackbar = useSnackbar();

    const onAvatarImageChange = (event: React.ChangeEvent<HTMLInputElement>, index: any) =>{
      event.persist();
      const files = event.target.files;
      if (files && files.length != 0) {
        const reader = new FileReader();
        const file = files[0];
        reader.onloadend = () => {
          formikRef.current.setFieldValue(`terms[${index}].image`, {
            file,
            prevImage: reader.result,
            isNew: true,
          });
        };
        reader.readAsDataURL(file);
      } else {
        formikRef.current.setFieldValue('terms', {
          workoutTermsName: '',
          image: { file: null, prevImage: '', isNew: null },
          description: '',
        });
      }
    }

    const onImageChange = (event: React.ChangeEvent<HTMLInputElement>) =>{
      event.persist();
      const files = event.target.files;
      if (files && files.length != 0) {
        const reader = new FileReader();
        const file = files[0];
        reader.onloadend = () => {
          formikRef.current.setFieldValue('image', {
            file,
            prevImage: reader.result,
            isNew: true,
          });
        };
        reader.readAsDataURL(file);
      } else {
        formikRef.current.setFieldValue('image', {
          workoutTermsName: '',
          image: { file: null, prevImage: '', isNew: null },
          description: '',
        });
      }
    }

    const onSubmit = (value: any,helper: any) =>{
      try{
        helper.setSubmitting(true)
        const renderSubmit = async () =>{
          const { image, terms, ...rest} = value;
          const { isNew, file} = image;
          const postData = rest;
          const uploadTermsImage = terms.map(async (data: any)=>{
            if(data?.image?.isNew){
              const getUploadedImage = await imageUpload(data.image.file);
              data.image = getUploadedImage;
              return data;
            }
            else {
              data.image = await data.image.file;
              return data;
            }
          })

          let tempdatas = Promise.all(uploadTermsImage).then(
            (dataWithImageUrl: any) => {
              postData.terms = dataWithImageUrl;
            }
          );

          if(isNew){
            postData.image = await imageUpload(file)
          }
          else {
            postData.image = file;
          }
          !isEdit && addData(postData, helper);
          isEdit && editData(postData, helper);
        }
        renderSubmit()
      }
      catch{
        Snackbar.show('Image Upload Failed', 'error');
      }
    }

    const addData = async (value: any, { setSubmitting, resetForm }: any) => {
      await console.log(value)
    //  await Post('app/addMealRecipe', value)
    //     .then((res: any) => {
    //       Snackbar.show(res.message, 'success');
    //       setSubmitting(false);
    //       resetForm();
    //       onSuccess();
    //     })
    //     .catch((err: any) => {
    //       Snackbar.show(err.message, 'error');
    //     });
    };
  
    const editData = async (data: any, { setSubmitting, resetForm }: any) => {
      // setSubmitting(true);
      console.log(data)
    //  await Post('app/editMealRecipe', data)
    //     .then((res: any) => {
    //       Snackbar.show(res.message, 'success');
    //       setSubmitting(false);
    //       resetForm();
    //       onSuccess();
    //     })
    //     .catch((err: any) => {
    //       Snackbar.show(err.message, 'error');
    //     });
    };

    const listAllMealRecipe = () =>{
      Post('app/listAllMealIngredient', {})
      .then((res: any) => {
        if (!res.error) {
          let getequipmentList = res.data.map((data: any) => {
            data.id = data._id;
            return data;
          });
          setAllIngredients(getequipmentList);
        } else {
          Snackbar.show(res.message, 'error');
        }
      })
      .catch((err: any) => {
        Snackbar.show(err.message, 'error');
      });
    }

    React.useEffect(()=>{
      listAllMealRecipe();
    },[])

    React.useEffect(()=>{
      if(isEdit){
        const {terms, image, _id, ingredients, ...rest} = data;
        const editData = {...rest, id: _id};
        editData.image = { file: image, prevImage: image, isNew: false };
        editData.ingredients = ingredients.map((data: any)=>{
          return {
            id : data._id,
            quantity: data.quantity
        }
        })

        editData.terms = terms.map((items: any)=>{
          return {
            name : items.name,
            image : { file:items.image , prevImage : items.image, isNew: false},
            term : items.term
        }
        })
        setInitialValues(editData)
      } 
      else {
        setInitialValues(initialFormValues)
      }
    },[props])

    const addMealRecipeTerms = (values:MealPlan, setFieldValue: any) =>{
      const { terms } = values
      terms.push(mealTerms);
      setFieldValue('terms',terms)
    }

    const removeMealRecipeTerms = (values: MealPlan, index: any, items: any) =>{
      const { terms } = values;
      const sampleDummyArray = [...terms];
      sampleDummyArray.splice(sampleDummyArray.indexOf(items), 1)
      formikRef.current.setFieldValue('terms',sampleDummyArray);
    }

    const addIngredients = (values:MealPlan, setFieldValue: any) =>{
      const { ingredients } = values;
      ingredients.push(ingredientsSelect)
      setFieldValue('ingredients',ingredients)
    }
    
    const handleOptionChange = (event: React.ChangeEvent<HTMLInputElement>, value : any, index: any) =>{
      formikRef.current.setFieldValue(`ingredients[${index}].id`,value?.id);
    }

    return (
        <>
        <Dialog
            open={isOpen}
            disableBackdropClick
            disableEscapeKeyDown
            fullWidth
            maxWidth='md'
            aria-labelledby='dialog-title'
        >
            <DialogTitle id='dialog-title' onClose={onClose}>
             {title}
            </DialogTitle>
            <Formik
                innerRef={formikRef}
                enableReinitialize
                initialValues={initialValues}
                onSubmit={onSubmit}
                validationSchema={Yup.object().shape({
                    name: Yup.string().trim().required('Name is required'),
                    nutrition: Yup.string().trim().required('Nutrition is required'),
                    description: Yup.string().trim().required('Description is required'),
                    protein: Yup.string().trim().required('Protein is required'),
                    fat: Yup.string().trim().required('Fat is required'),
                    carbs: Yup.string().trim().required('Carbs is required'),
                    terms : Yup.array().of(
                        Yup.object().shape({
                            name: Yup.string()
                              .trim()
                              .required('Meal recipe name is Required'),
                            image: Yup.object({
                              file: Yup.mixed().required('required'),
                            }),
                            term: Yup.string()
                              .trim()
                              .required('Meal Recipe terms is Required'),
                        })
                      ),
                    ingredients : Yup.array().of(
                      Yup.object().shape({
                        id: Yup.string().trim().required('Ingredients is Required'),
                        quantity : Yup.string().trim().required('Quantity is Required')
                      })),
                    preparation_time : Yup.string().trim().required('Preparation time is required'),
                    preparation_description :  Yup.string().trim().required('Preparation description is required'),
                    image :  Yup.object({ file: Yup.mixed().required('A file is required'),
                    }),
                  })}
            >
                {({values, errors, touched, handleBlur,handleChange,setFieldValue, submitForm, isSubmitting,})=>(
                <>
                <DialogContent dividers>
                    <Grid container spacing = {2}>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Name'
                                name='name'
                                variant='outlined'
                                value={values.name}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.name && errors.name
                                )}
                                helperText={
                                  touched.name && errors.name
                                }
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Nutrition'
                                name='nutrition'
                                variant='outlined'
                                value={values.nutrition}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.nutrition && errors.nutrition
                                )}
                                helperText={
                                  touched.nutrition && errors.nutrition
                                }
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                multiline
                                label='Description'
                                name='description'
                                variant='outlined'
                                value={values.description}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.description && errors.description
                                )}
                                helperText={
                                  touched.description && errors.description
                                }
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Protein'
                                name='protein'
                                variant='outlined'
                                value={values.protein}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.protein && errors.protein
                                )}
                                helperText={
                                  touched.protein && errors.protein
                                }
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Fat'
                                name='fat'
                                variant='outlined'
                                value={values.fat}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.fat && errors.fat
                                )}
                                helperText={
                                  touched.fat && errors.fat
                                }
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Carbs'
                                name='carbs'
                                variant='outlined'
                                value={values.carbs}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.carbs && errors.carbs
                                )}
                                helperText={
                                  touched.carbs && errors.carbs
                                }
                            />
                        </Grid>  
                        <Grid item md={12} xs={12}>
                            <Button
                                fullWidth
                                className={classes.themeButton}
                                variant='contained'
                                color='default'
                                onClick={() => addMealRecipeTerms(values, setFieldValue)}
                                endIcon={<ControlPointIcon />}
                            >
                                Add Terms
                            </Button>
                        </Grid>

                        {values?.terms?.map((items: any, index: any)=>(
                                <Grid
                                key={index}
                                item
                                container
                                md={12}
                                xs={12}
                                direction='row'
                                spacing={2}
                              >
                                <Grid item md={1} xs={12}>
                                  <input
                                    ref={avatharimgRef}
                                    type='file'
                                    accept='.jpg,.png,jpeg'
                                    onChange={(e) => onAvatarImageChange(e, index)}
                                    onBlur={handleBlur}
                                    hidden
                                  />
                                  <Avatar
                                    className={classes.workouttermsavatar}
                                    variant='square'
                                    onClick={() => avatharimgRef.current.click()}
                                    src={items?.image?.prevImage}
                                  />
                                  <FormControl
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.image?.file &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.image?.file
                                    )}
                                  >
                                    <FormHelperText>
                                      {touched?.terms &&
                                        touched?.terms[index]?.image?.file &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.image?.file}
                                    </FormHelperText>
                                  </FormControl>
                                </Grid>
          
                                <Grid item md={5} xs={12}>
                                  <TextField
                                    fullWidth
                                    multiline
                                    label='Terms name'
                                    name={`terms[${index}].name`}
                                    variant='outlined'
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.name &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.name
                                    )}
                                    helperText={
                                      touched?.terms &&
                                      touched?.terms[index]?.name &&
                                      errors?.terms &&
                                      (errors?.terms[index] as any)?.name
                                    }
                                    value={items.name}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                  />
                                </Grid>
                                <Grid item md={5} xs={8}>
                                  <TextField
                                    fullWidth
                                    multiline
                                    label='Terms description'
                                    name={`terms[${index}].term`}
                                    variant='outlined'
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.term &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.term
                                    )}
                                    helperText={
                                      touched?.terms &&
                                      touched?.terms[index]?.term &&
                                      errors?.terms &&
                                      (errors?.terms[index] as any)?.term
                                    }
                                    value={items.term}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                  />
                                </Grid>
          
                                <Grid item md={1} xs={4}>
                                  <Button
                                    fullWidth
                                    className={classes.deleteButton}
                                    variant='contained'
                                    color='secondary'
                                    onClick={()=>removeMealRecipeTerms(values, index, items)}
                                  >
                                    <DeleteIcon />
                                  </Button>
                                </Grid>
                              </Grid>
                            ))}

                        <Grid item md={12} xs={12}>
                            <Button
                                fullWidth
                                className={classes.themeButton}
                                variant='contained'
                                color='default'
                                onClick={() => addIngredients(values, setFieldValue)}
                                endIcon={<ControlPointIcon />}
                            >
                                Add Ingredients
                            </Button>
                        </Grid>

                        {values?.ingredients?.map((items: any, index: any)=>(
                                <Grid
                                key={index}
                                item
                                container
                                md={12}
                                xs={12}
                                direction='row'
                                spacing={2}
                              >          
                                <Grid item md={5} xs={12}>
                                <Autocomplete
                                  fullWidth
                                  options={allIngredients}
                                  value={allIngredients.find(
                                    (data: any) => data._id == items.id
                                  )}
                                  getOptionLabel={(option: any) => option.name}
                                  getOptionSelected={(option) =>
                                    option.id == items.id
                                  }
                                  onChange={(event: any,  newValue)=>handleOptionChange(event, newValue, index)}
                                  onBlur={handleBlur}
                                  renderInput={(params) => (
                                    <TextField
                                      {...params}
                                      label='Ingredients'
                                      variant='outlined'
                                      error={Boolean(
                                        touched?.ingredients &&
                                        touched?.ingredients[index]?.id &&
                                        errors?.ingredients &&
                                        (errors?.ingredients[index] as any)?.id
                                      )}
                                      helperText={
                                        touched?.ingredients &&
                                        touched?.ingredients[index]?.id &&
                                        errors?.ingredients &&
                                        (errors?.ingredients[index] as any)?.id
                                      }
                                      inputProps={{
                                        ...params.inputProps,
                                      }}
                                    />
                                  )}
                                />
                                </Grid>
                                <Grid item md={6} xs={8}>
                                  <TextField
                                    fullWidth
                                    multiline
                                    label='Quantity'
                                    name={`ingredients[${index}].quantity`}
                                    variant='outlined'
                                    error={Boolean(
                                      touched?.ingredients &&
                                        touched?.ingredients[index]?.quantity &&
                                        errors?.ingredients &&
                                        (errors?.ingredients[index] as any)?.quantity
                                    )}
                                    helperText={
                                      touched?.ingredients &&
                                      touched?.ingredients[index]?.quantity &&
                                      errors?.ingredients &&
                                      (errors?.ingredients[index] as any)?.quantity
                                    }
                                    value={items.quantity}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                  />
                                </Grid>
          
                                <Grid item md={1} xs={4}>
                                  <Button
                                    fullWidth
                                    className={classes.deleteButton}
                                    variant='contained'
                                    color='secondary'
                                    // onClick={() =>
                                    //   removeIngredients(values, index, setFieldValue)
                                    // }
                                  >
                                    <DeleteIcon />
                                  </Button>
                                </Grid>
                              </Grid>
                        ))}

                        <Grid item xs={12} md={12}>
                            <TextField
                                fullWidth
                                label='Preparation Time'
                                name='preparation_time'
                                variant='outlined'
                                value={values.preparation_time}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.preparation_time && errors.preparation_time
                                )}
                                helperText={
                                  touched.preparation_time && errors.preparation_time
                                }
                            />
                        </Grid>                        
                        <Grid item xs={12} md={12}>
                            <TextField
                                fullWidth
                                multiline
                                label='Preparation Description'
                                name='preparation_description'
                                variant='outlined'
                                value={values.preparation_description}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                  touched.preparation_description && errors.preparation_description
                                )}
                                helperText={
                                  touched.preparation_description && errors.preparation_description
                                }
                            />
                        </Grid> 
                        <Grid item md={12} xs={12}>
                          <input
                            name='image'
                            ref={imageRef}
                            type='file'
                            accept='.jpg,.png,jpeg'
                            onChange={onImageChange}
                            onBlur={handleBlur}
                            hidden
                          />
                          <Button
                            fullWidth
                            className={classes.themeButton}
                            variant='contained'
                            color='default'
                            onClick={() => imageRef.current.click()}
                            startIcon={<CloudUploadIcon />}
                          >
                            Upload Image
                          </Button>
                          <FormControl
                            error={Boolean(
                              touched?.image?.file && errors?.image?.file
                            )}
                          >
                            <FormHelperText>
                              {touched?.image?.file &&
                                errors?.image?.file}
                            </FormHelperText>
                          </FormControl>
                        </Grid>    
                        <Grid item md={12} xs={12}>
                          {values.image?.prevImage && (
                            <img
                              className={classes.imageView}
                              src={values.image?.prevImage}
                            />
                          )}
                        </Grid>       
                    </Grid>
                </DialogContent>
                <DialogActions>
                    <Button onClick={onClose} variant='outlined' color='secondary'>
                      Cancel
                    </Button>

                    <Button
                      className={classes.themeButton}
                      onClick={() => submitForm()}
                      disabled={isSubmitting}
                      variant='outlined'
                    >
                      {isSubmitting ? (
                        <CircularProgress size={24} style={{color : 'white'}} />
                      ) : (
                        okBtnText
                       )}
                    </Button>
                  </DialogActions>
                </>
            )}
            </Formik>
         </Dialog>
        </>
    )
}

export const ViewModel = (props: any) =>{
  const { isOpen, title, onClose, data } = props;
  const classes = useStyles();
  const [formValue, setFormValue] = React.useState(data);
  React.useEffect(() => {
    setFormValue(data);
  }, [props]);
  console.log(formValue)
  return (
    <Dialog 
      disableBackdropClick
      disableEscapeKeyDown
      fullWidth
      maxWidth='md'
      aria-labelledby='dialog-view-title'
      open={isOpen}
    >
       <DialogTitle id='dialog-view-title' onClose={onClose}>
        {title}
      </DialogTitle>

      <DialogContent dividers>
        <div>
          <img
            src={formValue?.image}
            alt={'Workout image'}
            className={classes.imageView}
          />
        </div>
        <Paper style={{marginTop:'20px'}}>
          <Table>
            <TableBody>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>
                  <strong>{formValue?.name}</strong>
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Description</TableCell>
                <TableCell>
                  <strong>{formValue?.description}</strong>
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Nutrition</TableCell>
                <TableCell>
                  <strong>{formValue?.nutrition}</strong>
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Proteins</TableCell>
                <TableCell>
                  <strong>{formValue?.protein}</strong>
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Fat</TableCell>
                <TableCell>
                  <strong>{formValue?.fat}</strong>
                </TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Carbs</TableCell>
                <TableCell>
                  <strong>{formValue?.carbs}</strong>
                </TableCell>
              </TableRow>
            </TableBody>
          </Table>
        </Paper>

        <Typography variant='h5' align='left' style={{ color: '#41A58D' }} className={classes.textPrimary}>
          INGREDIENTS
        </Typography>

        <Grid
          container
          spacing={2}
          className={classes.ingrdientsGridMain}
          justify='center'
        >
          {formValue?.ingredients?.map((value: any, index: any) => (
            <Grid key={index} item xs={4} md={3}>
              <Avatar
                className={classes.ingredientsAvatarRoot}
                src={value?.image}
              />
              <Typography variant='h6' align='center'>
                {value?.name}
              </Typography>
              <Typography variant='subtitle2' align='center'>
                {value.description.length >= 25
                  ? `${value.description.substring(0, 30)}...`
                  : value.description}
              </Typography>
            </Grid>
          ))}
          {formValue?.workout_terms?.length == 0 && (
            <Grid item xs={12} md={12} className={classes.noIngredientsText}>
              <div>No Ingredients</div>
            </Grid>
          )}
        </Grid>
        <Typography variant='h6' align='left' className={classes.textSecondary}>{formValue?.workout_description}</Typography>

        

        <Typography variant='h5' align='left' style={{ color: '#41A58D' }} className={classes.textPrimary}>
          MEAL RECIPE TERMS
        </Typography>

        <Grid
          container
          spacing={2}
          className={classes.ingrdientsGridMain}
          justify='center'
        >
          {formValue?.terms?.map((value: any, index: any) => (
            <Grid key={index} item xs={4} md={3}>
              <Avatar
                className={classes.ingredientsAvatarRoot}
                src={value?.image}
              />
              <Typography variant='h6' align='center'>
                {value?.name}
              </Typography>
              <Typography variant='subtitle2' align='center'>
                {value.term.length >= 25
                  ? `${value.term.substring(0, 30)}...`
                  : value.term}
              </Typography>
            </Grid>
          ))}
          {formValue?.required_equipments?.length == 0 && (
            <Grid item xs={12} md={12} className={classes.noIngredientsText}>
              <div>No Ingredients</div>
            </Grid>
          )}
        </Grid>

        <Grid container>
            <Grid xs={12}>
                <Typography variant='h5' align='left' style={{ color: '#41A58D' }} className={classes.textPrimary}>
                  PREPARATION TIME
                </Typography>
                <Typography variant='body2' align='center' className={classes.timeText}>
                <TimerIcon fontSize='inherit' />
                <span>{formValue?.preparation_time}</span>
                </Typography>
            </Grid>
            <Grid item xs={12} md={12} className={classes.htmlContentGrid}>
              <div className={classes.htmlContent}>{formValue?.preparation_description}</div>
            </Grid>
        </Grid>
      </DialogContent>

      <DialogActions>
        <Button onClick={onClose} variant='outlined' color='secondary'>
          Close
        </Button>
      </DialogActions>

    </Dialog>
  )
}

export default MealRecipe
