import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import { Typography } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import CustomModel from '../../components/Model/Model';
import CloseIcon from '@material-ui/icons/Close';
import Grid from '@material-ui/core/Grid';
import Fade from '@material-ui/core/Fade';
import AddIcon from '@material-ui/icons/Add';
import Page from '../../components/Page/Page';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import IconButton from '@material-ui/core/IconButton';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import CustomuploadButton from '../../components/Buttons/UploadButton';
import CircularProgress from '@material-ui/core/CircularProgress';
import Tooltip from '@material-ui/core/Tooltip';
import {
  AddIngredientsApi,
  ListIngredientsApi,
  DeleteIngredientsApi,
} from '../../Services/IngredientsAPI/Index';
import firebase from 'firebase';
import 'firebase/firestore';

const useStyles = makeStyles((theme: any) => ({
  root: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3),
  },
  addIcon: {
    marginRight: theme.spacing(1),
  },
  header: {
    marginBottom: theme.spacing(3),
  },
  table: {
    width: '100%',
    margin: 'auto',
  },
  card: {
    backgroundColor: 'white',
    margin: 'auto',
  },
  addcategoriesStyle: {
    backgroundColor: theme.palette.green.main,
    cursor: 'pointer',
    color: 'white',
    '&:hover': {
      backgroundColor: theme.palette.green.main,
      color: 'white',
    },
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    width: '40%',
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    borderRadius: 10,
    padding: theme.spacing(2, 4, 3),
    outline: 'none',
    [theme.breakpoints.down('md')]: {
      width: '80%',
    },
  },
  additemsstyle: {
    fontSize: 20,
    fontWeight: 500,
  },
  ItemNamestyle: {
    width: '100%',
  },
  textfieldStyles: {
    paddingTop: theme.spacing(2),
    display: 'flex',
    justifyContent: 'space-between',
  },
  addButtonStyle: {
    backgroundColor: '#41A58D',
    color: 'white',
    width: '20%',
    textTransform: 'capitalize',
    '&:hover': {
      backgroundColor: '#41A58D',
      color: 'white',
    },
  },
  materialtextfieldstyle: {
    width: '45%',
  },
  viewmodelText: {
    textAlign: 'start',
    marginTop: 20,
    fontSize: 15,
    fontWeight: 500,
  },
  imgpreviewName: {
    display: 'flex',
    justifyContent: 'center',
    width: '100%',
    height: 200,
    marginTop: 20,
  },
  ImageStyle: {
    maxWidth: '100%',
    maxHeight: '100%',
  },
  textareastyle: {
    borderRadius: 5,
    width: '100%',
    height: '100px !important',
    paddingLeft: 4,
    paddingTop: 10,
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    outline: 'none',
  },

  uploadButtonStyle: {
    backgroundColor: 'white',
    color: theme.palette.green.main,
    border: `1px solid ${theme.palette.green.main}`,
  },
}));

function DashBoard() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(false);
  const [viewAndEditModel, setViewAndEditModel] = React.useState(false);
  const [viewAndDeleteData, setViewAndDeleteData] = React.useState<any>('');
  const [modelTitle2, setModelTitle2] = React.useState('');
  const [modelTitle, setModelTitle] = React.useState('');
  const [itemName, setItemName] = React.useState('');
  const [images, setImages] = React.useState<any>('');
  const [description, setDescription] = React.useState('');
  const [getFiles, setGetFiles] = React.useState<any>('');
  const [ingredientsData, setIngredientsData] = React.useState<any>('');

  const openAddCategoriesModel = (type: any, values: any) => {
    setOpen(!open);
    setModelTitle(type);
    if (values !== 'Add') {
      setItemName(values.name);
      setDescription(values.description);
      setImages(values.image);
    }
  };

  const imageUpload = async () => {
    return new Promise((resolve, reject) => {
      const storage = firebase.storage();
      storage
        .ref(`images/${getFiles.name}`)
        .put(getFiles)
        .then(async (imageResult) => {
          const download_url = await storage
            .ref(`images/${getFiles.name}`)
            .getDownloadURL();
          resolve(download_url);
          // setGetFirebaseUrl(download_url)
          let addIngredients = {
            name: itemName,
            image: download_url,
            description: description,
          };
          console.log(addIngredients);
          const storeIngredientsData = await AddIngredientsApi(
            addIngredients,
            onSuccessAddedIngredient
          );
        })
        .catch((err) => {
          reject(err);
        });
    });
  };

  const onSuccessAddedIngredient = () => {
    setOpen(!open);
    setIsLoading(false);
    setImages('');
    setDescription('');
    setItemName('');
    ingredientsList();
  };

  const addAndEditFuntion = (type: any) => {
    if (type === 'Add') {
      setIsLoading(true);
      imageUpload();
    } else {
      setOpen(!open);
    }
  };

  const viewAndDeleteModelOpen = (type: any, value: any) => {
    setViewAndEditModel(!viewAndEditModel);
    setModelTitle2(type);
    setViewAndDeleteData(value);
  };

  const handleImageUpload = (event: any) => {
    let reader = new FileReader();
    let file = event.target.files[0];
    reader.onloadend = () => {
      setImages(reader.result);
    };
    reader.readAsDataURL(file);
    setGetFiles(file);
  };

  const handleModelClose = () => {
    setOpen(!open);
    setImages('');
    setDescription('');
    setGetFiles('');
  };

  const ingredientsList = () => {
    let getList = {
      page_no: 1,
      page_limit: 10,
    };
    ListIngredientsApi(getList, onSuccessIngredientList);
  };

  const onSuccessIngredientList = (response: any) => {
    setIngredientsData(response.data);
  };

  React.useEffect(() => {
    ingredientsList();
  }, []);

  const confirmDeleteIngredients = () => {
    let deleteId = {
      id: viewAndDeleteData._id,
    };
    DeleteIngredientsApi(deleteId, onSuccessDeletIngredients);
  };

  const onSuccessDeletIngredients = () => {
    setViewAndEditModel(false);
    ingredientsList();
  };

  return (
    <div className={classes.root}>
      <Page title='Projects List' />
      <div className={classes.header}>
        <Grid
          alignItems='flex-end'
          container
          justify='space-between'
          spacing={3}
        >
          <Grid item>
            <Typography component='h1' variant='h3'>
              See the latest opportunities
            </Typography>
          </Grid>
          <Grid item>
            <Button
              variant='contained'
              onClick={() => openAddCategoriesModel('Add', 'Add')}
              className={classes.addcategoriesStyle}
            >
              <AddIcon className={classes.addIcon} />
              Add Items
            </Button>
          </Grid>
        </Grid>
      </div>

      <Modal
        open={open}
        onClose={handleModelClose}
        className={classes.modal}
        aria-labelledby='simple-modal-title'
        aria-describedby='simple-modal-description'
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <Typography className={classes.additemsstyle}>
              {modelTitle === 'Add' ? 'Add Items' : 'Edit Items'}
            </Typography>
            <CloseIcon
              onClick={() => {
                setOpen(!open);
                setImages('');
                setDescription('');
                setItemName('');
              }}
              style={{ float: 'right', marginTop: -25, cursor: 'pointer' }}
            />
            <div style={{ paddingTop: 20 }}>
              <TextField
                id='outlined-basic'
                label='Item Name'
                variant='outlined'
                value={itemName}
                onChange={(e) => setItemName(e.target.value)}
                className={classes.ItemNamestyle}
              />
            </div>
            <div style={{ paddingTop: 20 }}>
              <TextareaAutosize
                className={classes.textareastyle}
                rowsMin={3}
                placeholder='Description'
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>
            <div>
              <CustomuploadButton
                style={{
                  display: 'flex',
                  justifyContent: 'center',
                  paddingTop: 20,
                }}
                ButtonName={'Upload image'}
                onChange={handleImageUpload}
              />
            </div>
            {images === '' ? (
              ''
            ) : (
              <div className={classes.imgpreviewName}>
                <img
                  src={images}
                  alt={'Uploading Images'}
                  className={classes.ImageStyle}
                />
              </div>
            )}

            <div
              style={{
                display: 'flex',
                justifyContent: 'flex-end',
                paddingTop: '20px',
              }}
            >
              <Button
                style={{ marginRight: 20, width: '20%' }}
                className={classes.uploadButtonStyle}
                onClick={() => {
                  setOpen(!open);
                  setImages('');
                  setDescription('');
                  setItemName('');
                }}
              >
                Cancel
              </Button>
              <Button
                onClick={
                  modelTitle === 'Add'
                    ? () => addAndEditFuntion('Add')
                    : () => addAndEditFuntion('Edit')
                }
                className={classes.addButtonStyle}
              >
                {modelTitle === 'Add' ? (isLoading ? 'Adding' : 'Add') : 'Edit'}
                {isLoading ? (
                  <i
                    style={{ fontSize: 15, marginLeft: 20 }}
                    className='fas fa-spinner fa-pulse'
                  ></i>
                ) : (
                  ''
                )}
              </Button>
            </div>
          </div>
        </Fade>
      </Modal>
      <div className={classes.card}>
        <TableContainer>
          <Table className={classes.table} aria-label='simple table'>
            <TableHead>
              <TableRow>
                <TableCell>Ingredients Name</TableCell>
                <TableCell align='center'>Image</TableCell>
                <TableCell align='center'>Discription</TableCell>
                <TableCell align='center'>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {ingredientsData &&
                ingredientsData.map((value: any, index: any) => {
                  return (
                    <TableRow hover style={{ cursor: 'pointer' }} key={index}>
                      <TableCell component='th' scope='row'>
                        {value.name}
                      </TableCell>
                      <TableCell align='center'>
                        <Tooltip title={value.image} aria-label='add'>
                          <Typography>
                            {value.image.length >= 25
                              ? `${value.image.substring(0, 40)}...`
                              : value.image}
                          </Typography>
                        </Tooltip>
                      </TableCell>
                      <TableCell align='center'>
                        <Tooltip title={value.description} aria-label='add'>
                          <Typography>
                            {value.description.length >= 25
                              ? `${value.description.substring(0, 40)}...`
                              : value.description}
                          </Typography>
                        </Tooltip>
                      </TableCell>
                      <TableCell align='center'>
                        <div
                          style={{
                            display: 'flex',
                            justifyContent: 'space-evenly',
                          }}
                        >
                          <Tooltip title='View' arrow>
                            <IconButton style={{ padding: 5 }}>
                              <CenterFocusStrongIcon
                                style={{ color: '#41A58D' }}
                                onClick={() =>
                                  viewAndDeleteModelOpen('View', value)
                                }
                              />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title='Edit' arrow>
                            <IconButton style={{ padding: 5 }}>
                              <EditIcon
                                style={{ color: '#41A58D' }}
                                onClick={() =>
                                  openAddCategoriesModel('Edit', value)
                                }
                              />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title='Delete' arrow>
                            <IconButton style={{ padding: 5 }}>
                              <DeleteIcon
                                style={{ color: '#41A58D' }}
                                onClick={() =>
                                  viewAndDeleteModelOpen('Delete', value)
                                }
                              />
                            </IconButton>
                          </Tooltip>
                        </div>
                      </TableCell>
                    </TableRow>
                  );
                })}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
      <CustomModel
        open={viewAndEditModel}
        onCancel={() => setViewAndEditModel(false)}
      >
        <div>
          <Typography className={classes.additemsstyle}>
            {modelTitle2 === 'View' ? 'View Item' : 'Delete Item'}
          </Typography>
          {modelTitle2 === 'View' ? (
            <div>
              <Grid container>
                <Grid item xs={6}>
                  <Typography className={classes.viewmodelText}>
                    Dessert
                  </Typography>
                  <Typography className={classes.viewmodelText}>
                    Calories
                  </Typography>
                  <Typography className={classes.viewmodelText}>Fat</Typography>
                  <Typography className={classes.viewmodelText}>
                    Carbs
                  </Typography>
                  <Typography className={classes.viewmodelText}>
                    Protein
                  </Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography className={classes.viewmodelText}>
                    {viewAndDeleteData.name}
                  </Typography>
                  <Typography className={classes.viewmodelText}>
                    {viewAndDeleteData.Calories}
                  </Typography>
                  <div className={classes.imgpreviewName}>
                    <img
                      src={images}
                      alt={'Uploading Images'}
                      className={classes.ImageStyle}
                    />
                  </div>
                </Grid>
              </Grid>
            </div>
          ) : (
            <Typography style={{ fontSize: 18, paddingTop: 20 }}>
              Are you sure want to delete this item ?
            </Typography>
          )}
          <div
            style={{
              display: 'flex',
              justifyContent: 'flex-end',
              paddingTop: '20px',
            }}
          >
            {modelTitle2 === 'View' ? (
              <Button className={classes.addButtonStyle}>Ok</Button>
            ) : (
              <div>
                <Button
                  style={{
                    backgroundColor: 'white',
                    color: '#41A58D',
                    border: '2px solid #41A58D',
                    textTransform: 'capitalize',
                    marginRight: 40,
                  }}
                  onClick={() => setViewAndEditModel(false)}
                >
                  No
                </Button>
                <Button
                  style={{
                    backgroundColor: '#41A58D',
                    color: 'white',
                    border: '2px solid #41A58D',
                    textTransform: 'capitalize',
                  }}
                  onClick={confirmDeleteIngredients}
                >
                  Yes
                </Button>
              </div>
            )}
          </div>
        </div>
      </CustomModel>
    </div>
  );
}

export default DashBoard;
