import React from 'react'
import { Avatar, Button, Card, CardActions, CardContent, CircularProgress, Dialog, DialogActions, DialogContent, Grid, IconButton, makeStyles, Paper, Tab,  Table, TableBody, FormControl, FormHelperText, TableCell, TableContainer, TableHead, TableRow, TextField, Tooltip,  Divider, Typography, Tabs} from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import AddIcon from '@material-ui/icons/Add';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination, TabContext, TabList, TabPanel } from '@material-ui/lab';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { imageUpload } from '../../utils/FirebaseUtils';
import { Formik } from 'formik';
import * as Yup from 'yup';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import Page from '../../components/Page/Page';
import useConfModel from '../../hook/useConfModel';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';
import ControlPointIcon from '@material-ui/icons/ControlPoint';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';


const useStyles = makeStyles((theme: any)=>({
    root: {
        width: theme.breakpoints.values.lg,
        maxWidth: '100%',
        margin: '0 auto',
        padding: theme.spacing(3),
    },
    themeButton: {
        color: theme.palette.white,
        backgroundColor: theme.palette.green.main,
        '&:hover': {
          backgroundColor: theme.palette.green.dark,
        },
    },
    workouttermsavatar: {
        width: '100%',
        height: '100%',
    },
    deleteButton: {
        width: '100%',
        height: '50px',
    },
}))
function MealRecipe() {
  const classes = useStyles();
  const [addEditDialog, setAddEditDialog] = React.useState({
    isOpen: false,
    title: '',
    okBtnText: '',
    isEdit: false,
    data: {},
  });

  const openAddDialog = () => {
    setAddEditDialog((prevState: any) => ({
      ...prevState,
      isOpen: true,
      title: 'Add Meal Recipe',
      isEdit: false,
      okBtnText: 'Save',
    }));
  };

  const closeAddEditDialog = () =>{
    setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
  }


  return (
    <div className={classes.root}>
        <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
            <Grid item>
            <Typography component='h1' variant='h3'>
                Meal Recipe
            </Typography>
            </Grid>
            <Grid item>
            <Button
                variant='contained'
                onClick={() => openAddDialog()}
                className={classes.themeButton}
                startIcon={<AddIcon />}
            >
                Add
            </Button>
            </Grid>
        </Grid>

      {/* =============Search======== */}
        <Grid container spacing={3}>
            <Grid item>
                <Paper elevation={0}>
                    <TextField
                    fullWidth
                    size='small'
                    placeholder='Name'
                    variant='outlined'
                    name='state'
                    />
                </Paper>
            </Grid>
        </Grid>

        <AddEditModel
            {...addEditDialog}
            onClose={closeAddEditDialog}
            // onSuccess={onSuccessAction}
         />

    </div>
    )
}

interface MealTerms {
    name : string,
    image : {file:string | null, prevImage : string, isNew: boolean | null},
    term : string
}

interface Ingredients {
    id : string
}

interface MealPlan {
    name : string,
    image : {file:string | null, prevImage : string, isNew: boolean | null},
    nutrition : string,
    description : string,
    protein : string,
    fat : string,
    carbs : string,
    terms : MealTerms[],
    ingredients : Ingredients[],
    preparation_time : string,
    preparation_description : string

}

const initialFormValues: MealPlan = {
    name : '',
    image : { file:null, prevImage : '', isNew : null },
    nutrition : '',
    description : '',
    protein : '',
    fat : '',
    carbs : '',
    terms : [
        {
            name : '',
            image : { file:null , prevImage : '', isNew: null},
            term : ''
        }
    ],
    ingredients : [{ id: '' }],
    preparation_time : '',
    preparation_description : ''
}

export const AddEditModel = (props: any) =>{
    const { isEdit, isOpen, okBtnText = 'OK', onClose, data, title, onSuccess } = props;
    const classes = useStyles()
    const [initialValues, setInitialValues] = React.useState({...initialFormValues});
    const formikRef = React.useRef<any>(null);
    const imageRef = React.useRef<any>(null);
    const avatharimgRef = React.useRef<any>(null);

    const onSubmit = () =>{
        alert('done')
    }

    const addIngredients = (values:MealPlan, setFieldValue: any) =>{
      console.log(values)
    }
    return (
        <>
        <Dialog
            open={isOpen}
            disableBackdropClick
            disableEscapeKeyDown
            fullWidth
            maxWidth='md'
            aria-labelledby='dialog-title'
        >
            <DialogTitle id='dialog-title' onClose={onClose}>
             {title}
            </DialogTitle>
            <Formik
                innerRef={formikRef}
                enableReinitialize
                initialValues={initialFormValues}
                onSubmit={onSubmit}
                validationSchema={Yup.object().shape({
                    name: Yup.string().trim().required('Name is required'),
                    nutrition: Yup.string().trim().required('Nutrition is required'),
                    description: Yup.string().trim().required('Description is required'),
                    protein: Yup.string().trim().required('Protein is required'),
                    fat: Yup.string().trim().required('Fat is required'),
                    carbs: Yup.string().trim().required('Carbs is required'),
                    terms : Yup.array().of(
                        Yup.object().shape({
                            name: Yup.string()
                              .trim()
                              .required('Workout term name is Required'),
                            image: Yup.object({
                              file: Yup.mixed().required('required'),
                            }),
                            term: Yup.string()
                              .trim()
                              .required('Workout description is Required'),
                        })
                    )

                })}
            >
                {({values, errors, touched, handleBlur,handleChange,setFieldValue, submitForm, isSubmitting,})=>(
                <>
                <DialogContent dividers>
                    <Grid container spacing = {2}>
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Name'
                                name='name'
                                variant='outlined'
                                value={values.name}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Nutrition'
                                name='nutrition'
                                variant='outlined'
                                value={values.nutrition}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                multiline
                                label='Description'
                                name='description'
                                variant='outlined'
                                value={values.description}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Protein'
                                name='protein'
                                variant='outlined'
                                value={values.protein}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Fat'
                                name='fat'
                                variant='outlined'
                                value={values.fat}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>                        
                        <Grid item xs={12} md={6}>
                            <TextField
                                fullWidth
                                label='Carbs'
                                name='carbs'
                                variant='outlined'
                                value={values.carbs}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>  
                        <Grid item md={12} xs={12}>
                            <Button
                                fullWidth
                                className={classes.themeButton}
                                variant='contained'
                                color='default'
                                onClick={() => addIngredients(values, setFieldValue)}
                                endIcon={<ControlPointIcon />}
                            >
                                Add Terms
                            </Button>
                        </Grid>

                        {values?.terms?.map((items: any, index: any)=>(
                                <Grid
                                key={index}
                                item
                                container
                                md={12}
                                xs={12}
                                direction='row'
                                spacing={2}
                              >
                                <Grid item md={1} xs={12}>
                                  <input
                                    ref={avatharimgRef}
                                    type='file'
                                    accept='.jpg,.png,jpeg'
                                    // onChange={(e) => onAvatarImageChange(e, index)}
                                    onBlur={handleBlur}
                                    hidden
                                  />
                                  <Avatar
                                    className={classes.workouttermsavatar}
                                    variant='square'
                                    onClick={() => avatharimgRef.current.click()}
                                    src={items?.image?.prevImage}
                                  />
                                  <FormControl
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.image?.file &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.image?.file
                                    )}
                                  >
                                    <FormHelperText>
                                      {touched?.terms &&
                                        touched?.terms[index]?.image?.file &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.image?.file}
                                    </FormHelperText>
                                  </FormControl>
                                </Grid>
          
                                <Grid item md={5} xs={12}>
                                  <TextField
                                    fullWidth
                                    multiline
                                    label='Workout terms name'
                                    name={`terms[${index}].name`}
                                    variant='outlined'
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.name &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.name
                                    )}
                                    helperText={
                                      touched?.terms &&
                                      touched?.terms[index]?.name &&
                                      errors?.terms &&
                                      (errors?.terms[index] as any)?.name
                                    }
                                    value={items.name}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                  />
                                </Grid>
                                <Grid item md={5} xs={8}>
                                  <TextField
                                    fullWidth
                                    multiline
                                    label='Workout terms description'
                                    name={`terms[${index}].description`}
                                    variant='outlined'
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.term &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.description
                                    )}
                                    helperText={
                                      touched?.terms &&
                                      touched?.terms[index]?.term &&
                                      errors?.terms &&
                                      (errors?.terms[index] as any)?.description
                                    }
                                    value={items.term}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                  />
                                </Grid>
          
                                <Grid item md={1} xs={4}>
                                  <Button
                                    fullWidth
                                    className={classes.deleteButton}
                                    variant='contained'
                                    color='secondary'
                                    // onClick={() =>
                                    //   removeIngredients(values, index, setFieldValue)
                                    // }
                                  >
                                    <DeleteIcon />
                                  </Button>
                                </Grid>
                              </Grid>
                            ))}

                        <Grid item md={12} xs={12}>
                            <Button
                                fullWidth
                                className={classes.themeButton}
                                variant='contained'
                                color='default'
                                // onClick={() => addIngredients(values, setFieldValue)}
                                endIcon={<ControlPointIcon />}
                            >
                                Add Ingredients
                            </Button>
                        </Grid>

                        {values?.terms?.map((items: any, index: any)=>(
                                <Grid
                                key={index}
                                item
                                container
                                md={12}
                                xs={12}
                                direction='row'
                                spacing={2}
                              >          
                                <Grid item md={5} xs={12}>
                                  <TextField
                                    fullWidth
                                    multiline
                                    label='Workout terms name'
                                    name={`terms[${index}].name`}
                                    variant='outlined'
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.name &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.name
                                    )}
                                    helperText={
                                      touched?.terms &&
                                      touched?.terms[index]?.name &&
                                      errors?.terms &&
                                      (errors?.terms[index] as any)?.name
                                    }
                                    value={items.name}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                  />
                                </Grid>
                                <Grid item md={6} xs={8}>
                                  <TextField
                                    fullWidth
                                    multiline
                                    label='Workout terms description'
                                    name={`terms[${index}].description`}
                                    variant='outlined'
                                    error={Boolean(
                                      touched?.terms &&
                                        touched?.terms[index]?.term &&
                                        errors?.terms &&
                                        (errors?.terms[index] as any)?.description
                                    )}
                                    helperText={
                                      touched?.terms &&
                                      touched?.terms[index]?.term &&
                                      errors?.terms &&
                                      (errors?.terms[index] as any)?.description
                                    }
                                    value={items.term}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                  />
                                </Grid>
          
                                <Grid item md={1} xs={4}>
                                  <Button
                                    fullWidth
                                    className={classes.deleteButton}
                                    variant='contained'
                                    color='secondary'
                                    // onClick={() =>
                                    //   removeIngredients(values, index, setFieldValue)
                                    // }
                                  >
                                    <DeleteIcon />
                                  </Button>
                                </Grid>
                              </Grid>
                        ))}

                        <Grid item xs={12} md={12}>
                            <TextField
                                fullWidth
                                label='Preparation Time'
                                name='protein'
                                variant='outlined'
                                value={values.protein}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>                        
                        <Grid item xs={12} md={12}>
                            <TextField
                                fullWidth
                                label='Preparation Description'
                                name='fat'
                                variant='outlined'
                                value={values.fat}
                                onChange={handleChange}
                                onBlur={handleBlur}
                            />
                        </Grid>            
                    </Grid>
                </DialogContent>
                <DialogActions>
                    <Button onClick={onClose} variant='outlined' color='secondary'>
                      Cancel
                    </Button>

                    <Button
                      className={classes.themeButton}
                      onClick={() => submitForm()}
                      disabled={isSubmitting}
                      variant='outlined'
                    >
                      {isSubmitting ? (
                        <CircularProgress size={24} style={{color : 'white'}} />
                      ) : (
                        okBtnText
                      )}
                    </Button>
                  </DialogActions>
                </>
            )}
            </Formik>
         </Dialog>
        </>
    )
}

export default MealRecipe
