import React, { useRef, useState } from 'react'
import { Avatar, Button, Card, CardActions, CardContent, CircularProgress, Dialog, DialogActions, DialogContent, Grid, IconButton, makeStyles, Paper, Tab, Table, TableBody, FormControl, FormHelperText, TableCell, TableContainer, TableHead, TableRow, TextField, Tooltip, Divider, Typography, Tabs } from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import AddIcon from '@material-ui/icons/Add';
import CenterFocusStrongIcon from '@material-ui/icons/CenterFocusStrong';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Pagination, TabContext, TabList, TabPanel } from '@material-ui/lab';
import TipTapEditor from '../../components/TipTapEditor/TipTapEditor/TipTapEditor';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { imageUpload } from '../../utils/FirebaseUtils';
import { Formik, getIn, useFormikContext } from 'formik';
import * as Yup from 'yup';
import DialogTitle from '../../components/DialogTitlle/DialogTitle';
import { TableLoader, TableNoData } from '../../components/Loader/Loader';
import Page from '../../components/Page/Page';
import useConfModel from '../../hook/useConfModel';
import useService from '../../hook/useService';
import useSnackbar from '../../hook/useSnackbar';
import ControlPointIcon from '@material-ui/icons/ControlPoint';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import TimerIcon from '@material-ui/icons/Timer';
import CarbsImages from '../../assets/Images/Carbs.png'
import ProteinsImages from '../../assets/Images/Protein.png'
import FatImages from '../../assets/Images/Fat.png'
import { useEffect } from 'react';
import UnitSelect from '../../components/UnitSelect/UnitSelect';
import UnitDropdown from '../../utils/MetricUnits';
import LikeImage from '../../assets/Images/like.png'
import CommentImage from '../../assets/Images/comment.png';
import CheckIcon from '@material-ui/icons/Check';


const useStyle = makeStyles((theme: any)=>({
    root: {
        width: theme.breakpoints.values.lg,
        maxWidth: '100%',
        margin: '0 auto',
        padding: theme.spacing(3),
    },
    themeButton: {
        color: theme.palette.white,
        backgroundColor: theme.palette.green.main,
        '&:hover': {
          backgroundColor: theme.palette.green.dark,
        },
    },
    workouttermsavatar: {
        width: '100%',
        height: '100%',
    },
    deleteButton: {
        width: '100%',
        height: '50px',
    },
    timeText: {
        display: 'flex',
        alignItems: 'center'
    },
    content: {
        padding: 0,
    },
    inner: {
        minWidth: 700,
    },
    actions: {
        padding: theme.spacing(1),
        justifyContent: 'center',
    },
    tabCard: {
        marginTop: theme.spacing(3),
    },
    sEvenly: {
        display: 'flex',
        justifyContent: 'space-evenly',
    },
    iconPadd: {
        padding: 5,
    },
    jCenter: {
        display: 'flex',
        justifyContent: 'center',
    },
    imageView: {
        width: '100%',
        height: '100%',
    },
    avatarRoot: {
        borderRadius: 10,
        marginRight: 15,
        width: theme.spacing(10),
        height: theme.spacing(10),
    },
    textPrimary: {
        marginTop: 10,
        fontWeight: 'bold',
    },
    textSecondary: {
        marginTop: 10,
        color: '#f0c100',
    },
    ingrdientsGridMain: {
        marginTop: 10,
    },
    ingredientsAvatarRoot: {
        width: theme.spacing(7),
        height: theme.spacing(7),
        margin: 'auto',
    },
    htmlContentGrid: {
        paddingLeft: theme.spacing(3),
        paddingRight: theme.spacing(3),
        paddingTop: theme.spacing(1),
    },
    noIngredientsText: {
        marginBottom: theme.spacing(3),
        display: 'flex',
        justifyContent: 'center',
    },
    textareaAdornedEnd: {
        paddingRight: 0
    },
    htmlContent: {
        '& ul': {
          paddingLeft: '1.2rem',
        },
        '& p': {
          textAlign: 'justify',
        },
    },
}));

function Blogs() {
    const classes = useStyle();
    const [isLoading, setIsLoading] = React.useState(false);
    const [viewDialog, setViewDialog] = React.useState({
        isOpen: false,
        title: '',
        data: {},
    });
    const [addEditDialog, setAddEditDialog] = useState({
        isOpen: false,
        title: '',
        okBtnText: '',
        isEdit: false,
        data: {},
    });

    const openAddDialog = () => {
        setAddEditDialog((prevState: any) => ({
          ...prevState,
          isOpen: true,
          title: 'Add Nutrition Plan',
          isEdit: false,
          okBtnText: 'Save',
        }));
      };
    
    const openEditDialog = (data: any) => {
        setAddEditDialog((prevState: any) => ({
          ...prevState,
          isOpen: true,
          isEdit: true,
          data,
          title: 'Edit Nutrition Plan',
          okBtnText: 'Edit',
        }));
    };

    const openViewDialog = (data: any) => {
        setViewDialog((prevState: any) => ({
          ...prevState,
          isOpen: true,
          data,
          title: 'View Blog Content',
        }));
    };

    const closeAddEditDialog = () => {
        setAddEditDialog((prevState: any) => ({ ...prevState, isOpen: false }));
    };

    const onSuccessAction = () => {
        // listNutritionPlan();
        closeAddEditDialog();
    };

    const closeViewDialog = () => {
        setViewDialog((prevState: any) => ({ ...prevState, isOpen: false }));
    };

    return (
        <div className = {classes.root}>
            <Page title='Blogs' />
            <Grid alignItems='flex-end' container justify='space-between' spacing={3}>
                <Grid item>
                <Typography component='h1' variant='h3'>
                    Meal Recipe
                </Typography>
                </Grid>
                <Grid item>
                <Button
                    variant='contained'
                    onClick={() => openAddDialog()}
                    className={classes.themeButton}
                    startIcon={<AddIcon />}
                >
                    Add
                </Button>
                </Grid>
            </Grid>

            {/* =============Search======== */}
            <Grid container spacing={3}>
                <Grid item>
                <Paper elevation={0}>
                    <TextField
                    fullWidth
                    size='small'
                    placeholder='Name'
                    variant='outlined'
                    name='state'
                    />
                </Paper>
                </Grid>
            </Grid>

            <Card className={classes.tabCard}>
                <CardContent className={classes.content}>
                <TableContainer>
                    <Table>
                    <TableHead>
                        <TableRow>
                        <TableCell align='center'>#</TableCell>
                        <TableCell align='center'>Image</TableCell>
                        <TableCell align='center'>Title</TableCell>
                        <TableCell align='center'>Description</TableCell>
                        <TableCell align='center'>Action</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {!isLoading &&
                        ['blogs1'].map((data: any, index: number) => (
                            <TableRow hover key={index}>
                            <TableCell align='center'>
                                {/* {stateData.page_limit * (stateData.page_no - 1) +
                                index +
                                1} */}1
                            </TableCell>
                            <TableCell align='center'>
                                <div className={classes.jCenter}>
                                <Avatar variant='square' src={data?.image} />
                                </div>
                            </TableCell>
                            <TableCell align='center'>{data}</TableCell>
                            <TableCell align='center'>{data}</TableCell>
                            <TableCell align='center'>
                                <div className={classes.sEvenly}>
                                <Tooltip title='View' arrow>
                                    <IconButton
                                    className={classes.iconPadd}
                                    onClick={() => openViewDialog(data)}
                                    >
                                    <CenterFocusStrongIcon color='primary' />
                                    </IconButton>
                                </Tooltip>
                                <Tooltip title='Edit' arrow>
                                    <IconButton
                                    className={classes.iconPadd}
                                    // onClick={() => openEditDialog(data)}
                                    >
                                    <EditIcon color='action' />
                                    </IconButton>
                                </Tooltip>
                                <Tooltip title='Delete' arrow>
                                    <IconButton
                                    className={classes.iconPadd}
                                    // onClick={() => onDelete(data)}
                                    >
                                    <DeleteIcon color='secondary' />
                                    </IconButton>
                                </Tooltip>
                                </div>
                            </TableCell>
                            </TableRow>
                        ))}
                        {isLoading && <TableLoader />}
                        {!isLoading && ['',''].length == 0 && (
                        <TableNoData>No Data Found</TableNoData>
                        )}
                    </TableBody>
                    </Table>
                </TableContainer>
                </CardContent>

                <CardActions className={classes.actions}>
                <Pagination
                    // count={pageCount}
                    // page={stateData.page_no}
                    // onChange={onPageChange}
                />
                </CardActions>
            </Card>
            <AddEditModel {...addEditDialog} onClose={closeAddEditDialog} onSuccess={onSuccessAction} />

            <ViewModel {...viewDialog} onClose={closeViewDialog} />
        </div>
    )
}

const initialValues = {
    description : '',
    blog_type : '',
    title : '',
    image : {File: null, prevImage : '' , isNew : null},
    image_thumbnail : {File: null, prevImage : '' , isNew : null},
}

export const AddEditModel = (props: any) =>{
    const { isEdit, isOpen, okBtnText = 'OK', onClose, data, title, onSuccess } = props;
    const [initialFormValues, setInitialFormValues] = React.useState({ ...initialValues});
    const formikRef = React.useRef<any>(null);

    const onSubmit = () =>{
        alert('done')
    }

    return (
        <Dialog
            open={isOpen}
            disableBackdropClick
            disableEscapeKeyDown
            fullWidth
            maxWidth='md'
            aria-labelledby='dialog-title'
        >
            <DialogTitle id='dialog-title' onClose={onClose}>
            {title}
            </DialogTitle>
            <Formik
                innerRef={formikRef}
                enableReinitialize
                initialValues={initialValues}
                onSubmit={onSubmit}
                validationSchema={Yup.object().shape({
                    description : Yup.string().trim().required('Blog Description is required'),
                    blog_type : Yup.string().trim().required('Blog Type is required'),
                    title : Yup.string().trim().required('Blog Title is required'),
                    image: Yup.object({ file: Yup.mixed().required('A file is required')}),
                    image_thumbnail: Yup.object({ file: Yup.mixed().required('A file is required')}),
                })}
            >
                {({ values, errors, touched, handleBlur, handleChange, setFieldValue, submitForm, setFieldTouched, isSubmitting, }) => (
                    <>
                    <DialogContent dividers>
                        <Grid container spacing={2}>
                            <Grid item xs={12} md={6}>
                                <TextField
                                fullWidth
                                label='Title'
                                name='title'
                                variant='outlined'
                                value={values.title}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                error={Boolean(
                                    touched.title && errors.title
                                )}
                                helperText={
                                    touched.title && errors.title
                                }
                                />
                            </Grid>
                        </Grid>
                    </DialogContent>
                    </>
                )}
            </Formik>
        </Dialog>
    )
}

export const ViewModel = (props: any) =>{
    const { isOpen, title, onClose, data } = props;
    const classes = useStyle();
    const [formValue, setFormValue] = React.useState(data);
    React.useEffect(() => {
      setFormValue(data);
    }, [props]);
  
    return (
      <Dialog
        disableBackdropClick
        disableEscapeKeyDown
        fullWidth
        maxWidth='sm'
        aria-labelledby='dialog-view-title'
        open={isOpen}
      >
        <DialogTitle id='dialog-view-title' onClose={onClose}>
          {title}
        </DialogTitle>
  
        <DialogContent dividers>
            <div>
                <img
                    src='https://5.imimg.com/data5/IM/KH/GLADMIN-21954819/south-indian-full-meals-250x250.jpg'
                    alt={'Workout image'}
                    className={classes.imageView}
                />
            </div>
            <Button style={{backgroundColor : '#F2805E',color : 'white',textTransform : 'capitalize',marginLeft : '15px'}}>Meals</Button>
            <List>
              <ListItem>
                <ListItemText 
                    primary={<Typography variant='h5'><strong>Benefits of eating Vegan food items</strong></Typography>} 
                    secondary={<Typography variant='subtitle2'>by <span style={{color : '#41A58D'}}>Dr. John Doe</span> . Mar 16, 2021</Typography>}
                />
              </ListItem>
            </List>
            <List>
              <ListItem>
                <ListItemText 
                    primary='Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'
                />
              </ListItem>
            </List>
            <Divider />
            <List>
              <ListItem>
                  <ListItemIcon>
                      <img src={LikeImage} alt='like' width='60%' />
                  </ListItemIcon>
                  <ListItemText primary='24 Likes' />
                  <ListItemIcon>
                      <img src={CommentImage} alt='Comment' width='60%' />
                  </ListItemIcon>
                  <ListItemText primary='24 Comments' />
              </ListItem>  
            </List>
            <Divider />
            <Typography className={classes.ingrdientsGridMain} variant='h4'><strong>Comments</strong></Typography>
            <List>
              <ListItem>
                  <ListItemAvatar>
                      <Avatar />
                  </ListItemAvatar>
                  <ListItemText 
                    primary={<Typography variant='subtitle2'>Dr. John Doe{' '}. 1hr</Typography>} 
                    secondary={<Typography variant='subtitle2'>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</Typography>}
                />
                <IconButton className={classes.themeButton}>
                    <CheckIcon />
                </IconButton>
                <IconButton style={{backgroundColor : 'red',color : 'white',marginLeft : '10px'}}>
                    <DeleteIcon />
                </IconButton>
              </ListItem>  
            </List>
        </DialogContent>
  
        <DialogActions>
          <Button onClick={onClose} variant='outlined' color='secondary'>
            Close
          </Button>
        </DialogActions>
  
      </Dialog>
    )
}

export default Blogs
